function btnReqIssue_OnClick(obj)
{
	if(!fcAccCheck()){
		return;
	}
	dsOutIncomIssu.InsertRow(0);

	dsOutIncomIssu.setColumn(dsOutIncomIssu.rowposition, 'REQ_DEPT_CD', cboDeptCd.Value);
	dsOutIncomIssu.setColumn(dsOutIncomIssu.rowposition, 'REQ_USER_ID', gdsUser.GetColumn(gdsUser.rowposition, "USER_ID"));
	dsOutIncomIssu.setColumn(dsOutIncomIssu.rowposition, 'REQ_USER_NM', gdsUser.GetColumn(gdsUser.rowposition, "USER_NM"));
	dsOutIncomIssu.setColumn(dsOutIncomIssu.rowposition, 'REQ_YEAR', cboReqYear.Value);
	dsOutIncomIssu.setColumn(dsOutIncomIssu.rowposition, 'ISSU_CNT', edIssuCnt.Text);
	dsOutIncomIssu.setColumn(dsOutIncomIssu.rowposition, 'USE_GOAL', edUseGoal.Text);
	dsOutIncomIssu.setColumn(dsOutIncomIssu.rowposition, 'SUBMIT_OFFICE', edSubmitOffice.Text);
	dsOutIncomIssu.setColumn(dsOutIncomIssu.rowposition, 'RACER_NO', edRacerNo.Text);
	dsOutIncomIssu.setColumn(dsOutIncomIssu.rowposition, 'RACER_NM', edRacerNm.Text);

	fcSave();
}


function btnMultiReqIssue_OnClick(obj)
{
	if(!fcMultiCheck()){
		return;
	}

	var sArg = "Arg_sDeptCd=" + quote(cboDeptCd.Value)
	         + " Arg_sUserId=" + quote(gdsUser.GetColumn(gdsUser.rowposition, "USER_ID"))
	         + " Arg_sUserNm=" + quote(gdsUser.GetColumn(gdsUser.rowposition, "USER_NM"))
	         + " Arg_sReqYear=" + quote(cboReqYear.Value)
	         + " Arg_sIssuCnt=" + quote(edIssuCnt.Text)
	         + " Arg_sUseGoal=" + quote(edUseGoal.Text)
	         + " Arg_sSubmitOffice=" + quote(edSubmitOffice.Text);

	var sResponseCnt = Dialog("form::E07040022.xml", sArg, 834, 450, "");

	if ( sResponseCnt > 0 ) fcSave();
}

function fcPrintTaxReport(){
	var incom_issu_no = dsOutIncomIssu.getColumn(dsOutIncomIssu.rowposition, 'INCOM_ISSU_NO');
	var req_year = dsOutIncomIssu.getColumn(dsOutIncomIssu.rowposition, 'REQ_YEAR');
	var issu_cnt = dsOutIncomIssu.getColumn(dsOutIncomIssu.rowposition, 'ISSU_CNT');
	var racer_no = dsOutIncomIssu.getColumn(dsOutIncomIssu.rowposition, 'RACER_NO');
	var racer_nm = dsOutIncomIssu.getColumn(dsOutIncomIssu.rowposition, 'RACER_NM');
	var use_goal = dsOutIncomIssu.getColumn(dsOutIncomIssu.rowposition, 'USE_GOAL');
	var submit_office = dsOutIncomIssu.getColumn(dsOutIncomIssu.rowposition, 'SUBMIT_OFFICE');
	var issue_cnt = dsOutIncomIssu.getColumn(dsOutIncomIssu.rowposition, 'ISSU_CNT');

	dsOutIncomIssu.setColumn(dsOutIncomIssu.rowposition, 'ISSU_YN', 'Y');
	http.sync=true;

	//데이터 검색 
	buildTaxReportDataSet(req_year, racer_no);
	dsOutIncomIssuReport.ClearData();
	for(i=0; i<issu_cnt; i++){
		dsOutIncomIssuReport.AddRow();
		var report_incom_issu_no = incom_issu_no +  fcLPAD(i+1, 3, '0');
		dsOutIncomIssuReport.SetColumn(dsOutIncomIssuReport.rowposition, 'INCOM_ISSU_NO', report_incom_issu_no);
		dsOutIncomIssuReport.SetColumn(dsOutIncomIssuReport.rowposition, 'RACER_NO', racer_no);
		dsOutIncomIssuReport.SetColumn(dsOutIncomIssuReport.rowposition, 'RACER_NM', racer_nm);
		dsOutIncomIssuReport.SetColumn(dsOutIncomIssuReport.rowposition, 'USE_GOAL', use_goal);
		dsOutIncomIssuReport.SetColumn(dsOutIncomIssuReport.rowposition, 'SUBMIT_OFFICE', submit_office);
		dsOutIncomIssuReport.SetColumn(dsOutIncomIssuReport.rowposition, 'ISSU_CNT', issue_cnt);
	}

	//프린트 
	fcPrint();
	//프린트 flag 체크 
	fcSavePrntCheck();

	http.sync=false;

}


function fcLPAD(str, len, fillChar){
	str = "" + str;
	var tempStr = "";
	if(str == "")return "";
	var strLength = str.length();
	for(index=0; index< (len-strLength); index++){
		tempStr += fillChar;
	}
	return (tempStr + str);
}

function fcPrint(){
	var sealmgUrl = GBL_REPORT_URL+"seal2.jpg";
	var logImgUrl = GBL_REPORT_URL+"log.jpg";
	var sSubUrl   = "";                     //레포트 파일이 있는 서브디렉토리
	var sRptNm    = "e07039030_tax_report2.jrf";               //레포트 파일명
	var sDataSet  = "dsOutComInfo=dsOutComInfo dsOutRacerComInfo=dsOutRacerComInfo dsOutPrintMonthL=dsOutPrintMonthL dsOutPrintMonthR=dsOutPrintMonthR dsOutIncomIssuReport=dsOutIncomIssuReport";    //"UBI 쿼리명, MIP Dataset명"

	var sArg  = "NM_KOR="+dsOutRacerComInfo.getColumn(0, "NM_KOR");		
	sArg += " SEAL_IMG_URL="+ sealmgUrl;

	var sRptDrct  = 1;                               //출력방향(0 : 수직, 1 : 수평)	
	fnc_RPTPrint(sSubUrl, sRptNm, sDataSet, sArg, sRptDrct);


}

function buildTaxReportDataSet(req_year, racer_no) {

	var sServiceName = "e07030030-service:searchTaxMonthPay";
    var sInDataSet   = "";
    var sOutDataSet  = "dsOutComInfo=dsOutComInfo dsOutRacerComInfo=dsOutRacerComInfo dsOutMonthPay=dsOutMonthPay";
    fce_AddParam("STND_YEAR", req_year);	
    fce_AddParam("RACER_NO", racer_no);
    fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBackTax", false);	

}

function fcPrintSearch(suppAmt,incomeAmt,inhabAmt) {
	var countl = 0;
	var countr = 0;
	dsOutPrintMonthL.ClearData();
	dsOutPrintMonthR.ClearData();

	for(var i = 1 ; i <= dsOutMonthPay.GetRowCount(); i++) {
		if(i ==1 || i == 3 || i == 5 || i == 7 || i == 9 || i == 11 || i ==13){
			dsOutPrintMonthL.AddRow(); 
			dsOutPrintMonthL.SetColumn(countl,"AID_YEAR",dsOutMonthPay.GetColumn(i-1, "AID_YEAR")+"."+dsOutMonthPay.GetColumn(i-1, "AID_MONTH"));
			dsOutPrintMonthL.SetColumn(countl,"AID_MONTH",dsOutMonthPay.GetColumn(i-1, "AID_MONTH"));			
			dsOutPrintMonthL.SetColumn(countl,"SUPP_AMT",dsOutMonthPay.GetColumn(i-1, "SUPP_AMT"));			
			dsOutPrintMonthL.SetColumn(countl,"INCOME_TAX",dsOutMonthPay.GetColumn(i-1, "INCOME_TAX"));			
			dsOutPrintMonthL.SetColumn(countl,"INHAB_TAX",dsOutMonthPay.GetColumn(i-1, "INHAB_TAX"));			
			dsOutPrintMonthL.SetColumn(countl,"PMT_YEAR",dsOutMonthPay.GetColumn(i-1, "PMT_YEAR")+"."+dsOutMonthPay.GetColumn(i-1, "PMT_MONTH"));			
			dsOutPrintMonthL.SetColumn(countl,"PMT_MONTH",dsOutMonthPay.GetColumn(i-1, "PMT_MONTH"));			
			countl++;
		}else if(i ==2 || i == 4 || i == 6 || i == 8 || i == 10 || i == 12 || i ==14){
			dsOutPrintMonthR.AddRow(); 
			dsOutPrintMonthR.SetColumn(countr,"AID_YEAR",dsOutMonthPay.GetColumn(i-1, "AID_YEAR")+"."+dsOutMonthPay.GetColumn(i-1, "AID_MONTH"));	
			dsOutPrintMonthR.SetColumn(countr,"AID_MONTH",dsOutMonthPay.GetColumn(i-1, "AID_MONTH"));			
			dsOutPrintMonthR.SetColumn(countr,"SUPP_AMT",dsOutMonthPay.GetColumn(i-1, "SUPP_AMT"));			
			dsOutPrintMonthR.SetColumn(countr,"INCOME_TAX",dsOutMonthPay.GetColumn(i-1, "INCOME_TAX"));			
			dsOutPrintMonthR.SetColumn(countr,"INHAB_TAX",dsOutMonthPay.GetColumn(i-1, "INHAB_TAX"));			
			dsOutPrintMonthR.SetColumn(countr,"PMT_YEAR",dsOutMonthPay.GetColumn(i-1, "PMT_YEAR")+"."+dsOutMonthPay.GetColumn(i-1, "PMT_MONTH"));			
			dsOutPrintMonthR.SetColumn(countr,"PMT_MONTH",dsOutMonthPay.GetColumn(i-1, "PMT_MONTH"));			
			countr++;
		}	


	}
	dsOutPrintMonthL.AddRow(); 
	dsOutPrintMonthL.SetColumn(countl,"AID_YEAR","계");
	dsOutPrintMonthL.SetColumn(countl,"SUPP_AMT",suppAmt);			
	dsOutPrintMonthL.SetColumn(countl,"INCOME_TAX",incomeAmt);			
	dsOutPrintMonthL.SetColumn(countl,"INHAB_TAX",inhabAmt);	


}


function fcCallBackTax(sServiceName, ErrorCode, ErrorMSG) {

	if ( !fnc_result(ErrorCode, ErrorMSG) ) return;

	// 조회 후
	if ( sServiceName == "e07030030-service:searchTaxMonthPay" ) {
		if(dsOutMonthPay.GetRowCount()>0){

			var suppAmt =0;
			var incomeAmt =0;
			var inhabAmt =0;
			for(var i = 1 ; i <= dsOutMonthPay.GetRowCount(); i++) {
				suppAmt = suppAmt+dsOutMonthPay.GetColumn(i-1, "SUPP_AMT");
				incomeAmt = incomeAmt+dsOutMonthPay.GetColumn(i-1, "INCOME_TAX");
				inhabAmt = inhabAmt+dsOutMonthPay.GetColumn(i-1, "INHAB_TAX");
			}
			fcPrintSearch(suppAmt,incomeAmt,inhabAmt);
	    }else{

	    }

	}

}
