/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(ServiceName, ErrorCode, ErrorMSG) {	
	if ( !fnc_result(ErrorCode, ErrorMSG) ) {
		fcSearch();
		return;
	}

	// 조회 후
	if ( ServiceName == "e07030052-service:searchRcptIssu" ) {
	    fnc_SetStatus(L_COM_CNF_0000,  dsRcptIssu.GetRowCount());

	}

	// 저장 후
	if ( ServiceName == "e07030052-service:updateRcptIssu" ) {
		fcSearch();
	}

	//오류가 없이 정상 처리되었다면...
	fcPostProceS(ServiceName);
}

