}

function dsRcptIssu_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	if(strColumnID == "PROG_STAT"){
		if(dsRcptIssu.GetColumn(nrow, "PROG_STAT") == "200"){
			if(fnc_Message(L_COM_CNF_0000, "Y", "승인처리 하시겠습니까?") == "1"){
				//승인처리
				var sServiceName = "e07030052-service:updateRcptIssu";
				var sInDataSet   = "";
				var sOutDataSet  = "";     

				fce_AddParam("ISSU_SEQ", dsRcptIssu.GetColumn(nrow, "ISSU_SEQ"));
				fce_AddParam("ISSU_YEAR", dsRcptIssu.GetColumn(nrow, "ISSU_YEAR"));
				fce_AddParam("RACER_NO", dsRcptIssu.GetColumn(nrow, "RACER_NO"));
				fce_AddParam("RES_NO", dsRcptIssu.GetColumn(nrow, "RES_NO"));
				fce_AddParam("PROG_STAT", dsRcptIssu.GetColumn(nrow, "PROG_STAT"));			
				fce_AddParam("RJT_RS", dsRcptIssu.GetColumn(nrow, "RJT_RS"));
				fce_AddParam("SEQ", dsRcptIssu.GetColumn(nrow, "SEQ"));

				fce_AddParam("NM_KOR", dsRcptIssu.GetColumn(nrow, "NM_KOR"));
				fce_AddParam("CELPON_NO", dsRcptIssu.GetColumn(nrow, "CELPON_NO"));

				fce_AddParam("CORP_REG_NO", dsCorpRegNo.GetColumn(0, "001"));

				fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);
			}
			else{
				//취소
				dsRcptIssu.SetColumn(nRow,"PROG_STAT","100");
				edTmp.SetFocus();
				return false;
			}
		}
		else if(dsRcptIssu.GetColumn(nrow, "PROG_STAT") == "300"){
			if(fnc_Message(L_COM_CNF_0000, "Y", "반려처리 하시겠습니까?") == "1"){
				if(length(dsRcptIssu.GetColumn(nrow, "RJT_RS"))==0){
					fnc_Message(L_COM_CNF_0000, "N", "반려사유를 입력하세요.");
					dsRcptIssu.SetColumn(nRow,"PROG_STAT","100");
					edTmp.SetFocus();
					return false;
				}

				//반려처리
				var sServiceName = "e07030052-service:updateRcptIssu";
				var sInDataSet   = "";
				var sOutDataSet  = "";     

				fce_AddParam("ISSU_SEQ", dsRcptIssu.GetColumn(nrow, "ISSU_SEQ"));
				fce_AddParam("ISSU_YEAR", dsRcptIssu.GetColumn(nrow, "ISSU_YEAR"));
				fce_AddParam("RACER_NO", dsRcptIssu.GetColumn(nrow, "RACER_NO"));
				fce_AddParam("RES_NO", dsRcptIssu.GetColumn(nrow, "RES_NO"));
				fce_AddParam("PROG_STAT", dsRcptIssu.GetColumn(nrow, "PROG_STAT"));			
				fce_AddParam("RJT_RS", dsRcptIssu.GetColumn(nrow, "RJT_RS"));
				fce_AddParam("SEQ", dsRcptIssu.GetColumn(nrow, "SEQ"));

				fce_AddParam("NM_KOR", dsRcptIssu.GetColumn(nrow, "NM_KOR"));
				fce_AddParam("CELPON_NO", dsRcptIssu.GetColumn(nrow, "CELPON_NO"));

				fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);
			}
			else{
				//취소
				dsRcptIssu.SetColumn(nRow,"PROG_STAT","100");
				edTmp.SetFocus();
				return false;
			}
		}
	}
}


function lFcPrint(url, parameters, DataSetId) 
{
	if(parameters.Trim().length() > 0){
		if(gdsParam.colcount > 0) gdsParam.Clear();

		var parameterArray = parameters.split(";");
		if(parameterArray.Length() > 0){
			gdsParam.AddColumn("PARAM_NM");
			gdsParam.AddColumn("PARAM_VAL");

			for(var i=0; i<parameterArray.Length(); i++) {
				var keyValueArray = parameterArray[i].Split("=");
				var aRow = gdsParam.AddRow();
				gdsParam.SetColumn(aRow, "PARAM_NM" ,keyValueArray[0]);
				gdsParam.SetColumn(aRow, "PARAM_VAL",keyValueArray[1]);
			}
		}
	}

	/* 레포트 배율 추가 - 시작 */
	var nRow = gdsParam.AddRow();
	// Default Scale 파라미터명 추가(고정)
	gdsParam.SetColumn(nRow, "PARAM_NM", "DEFAULT_SCALE");
	// Scale 값 추가(PARAM_VAL = 1 : 100 + 1 * 20 = 120%, PARAM_VAL = 2 : 100 + 2 * 20 = 140%, PARAM_VAL = 3 : 100 + 3 * 20 = 160%.....) 
	gdsParam.SetColumn(nRow, "PARAM_VAL", "1");
	/* 레포트 배율 추가 - 끝 */

	lFcReportPreView(url, gdsParam.ID);

}


/**
 * 레포트 미리보기 팝업 오픈(GateWay 방식 - SQL을 레포트에 포함)
 *
 * param rptFile   - 레포트 파일(ID)
 * param dsParamId - 파라미터 정의 Dataset ID
 * param fullSize  - 팝업 호출시 Screen Size에 꽉찬 상태로 호출할것인지 여부
 * return void
 */
function lFcReportPreView(rptFile, paramDsId, fullSize)
{
	var nWidth = 760;
	var nHeight = 832;
	var nPosX = 0;
	var nPosY = 0;
	var bFullSize;
	var fSQLCallFlag;

    if(rptFile==null || fnc_isNull(rptFile)) {
        fnc_Message("보고서 파일지 존재하지 않습니다.");
        return;
    }

    if (fullSize == null) {
        bFullSize = false;
    } else {
        bFullSize = true;
    }
    if (bFullSize) {
        nWidth  = getDeviceInfo("CXScreen") - 8;
        nHeight = getDeviceInfo("CYScreen") - 55;
        nPosX   = (getDeviceInfo("CXScreen")-nWidth-6)/2;
        nPosY   = (getDeviceInfo("CYScreen")-nHeight-56)/2;
    }

    var sArg = "rptFile=" + quote(rptFile);
    if(!fnc_IsNull(paramDsId)) {
        sArg += " paramDsId=" + quote(paramDsId);
    }

	sArg += " fSQLCallFlag=true";
    var bRtn = Open("common::comVrtRptViewer_Ifis.xml", sArg, nWidth, nHeight, "AutoSize=false Resize=true", nPosX, nPosY);

    return;
}



