/*-------------------------------------------------------------------------------------------------+
>>  취소 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcInit(obj) {	
	if(sInpt == "F"){
		this.divBtnLst.btnSave.Visible = false;
	}
    dsOutUnColList.ClearData();
    fce_AddCodeRow(dsOutStndMonth, '전체', '', '');
	cboStndMonth.Value = '';	
}

/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearchCheck() {
   if(!fnc_IsMandatory(cboStndYear, "기준년도")){
		return false;
   }
   return true;
}

//조회 
function fcSearch(obj) {
	if(!fcSearchCheck()){
		return;
	}
	if(cboCollCd.Value =='' ||cboCollCd.Value ==null){
		fcSearchUnCollTpe();
	}else{
		fcSearchUnColl();
	}    

}

//미수금 내역 조회 
function fcSearchUnColl(){
	fce_AddParam("STND_YEAR", cboStndYear.Value);	
    fce_AddParam("PAY_MONTH", cboStndMonth.Value);
    fce_AddParam("NM_KOR", edNmKor.Text);
    fce_AddParam("COLL_TPE", cboCollCd.Value);

    var sServiceName = "e07040010-service:searchUnCollList";
    var sInDataSet   = "";
    var sOutDataSet  = "dsOutUnColList=dsOutUnColList";
    fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}

//미수금 내역 조회 
function fcSearchUnCollTpe(){
	fce_AddParam("STND_YEAR", cboStndYear.Value);	
    fce_AddParam("PAY_MONTH", cboStndMonth.Value);
    fce_AddParam("NM_KOR", edNmKor.Text);    

    var sServiceName = "e07040010-service:searchUnCollTpeList";
    var sInDataSet   = "";
    var sOutDataSet  = "dsOutUnColList=dsOutUnColList";
    fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}
