/*-------------------------------------------------------------------------------------------------+
>>  저장 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSaveCheck(obj) {
	//변경된 자료가 있는지 체크
	if(dsOutUnColList.GetRowCount()==0){
			//저장 할 자료가 없음
		fnc_Message(SNIS_COM_1006, "N");	
		return false;

	}

	return true;

}

function fcSave(obj) {
	if ( !fcSaveCheck() ) return;
    fce_AddParam("STND_YEAR", dsOutUnColList.getColumn(0, "STND_YEAR"));	
    fce_AddParam("PAY_MONTH", dsOutUnColList.getColumn(0, "PAY_MONTH"));
    fce_AddParam("COUNT", dsOutUnColList.getColumn(0, "COUNT"));
    fce_AddParam("NM_KOR", edNmKor.Text);
    fce_AddParam("COLL_TPE", cboCollCd.Value);

    var sServiceName = "e07040010-service:saveUnCollList";
    var sInDataSet   = "dsOutUnColList=dsOutUnColList";
    var sOutDataSet  = "dsOutUnColList=dsOutUnColList";

   	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack",false);
}

