/***************************************************************************************************
*                                      사용자 정의  처리 부분                                      *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {

	if ( !fnc_result(ErrorCode, ErrorMSG) ) return;

	if ( sServiceName == "common-service:searchCode" ) {

    }else if( sServiceName == "e07040010-service:searchUnCollList" ) {
	    if(dsOutUnColList.GetRowCount()>0){
			fnc_SetStatus(SNIS_BOA_0003,  dsOutUnColList.GetRowCount());
	    }else{
			fnc_SetStatus(SNIS_BOA_0003,  dsOutUnColList.GetRowCount());
	    }
	}else if ( sServiceName == "e07040010-service:saveUnCollList" ) {

	}else if( sServiceName == "e07040010-service:searchUnCollTpeList" ) {
	    if(dsOutUnColList.GetRowCount()>0){
			fnc_SetStatus(SNIS_BOA_0003,  dsOutUnColList.GetRowCount());
	    }else{
			fnc_SetStatus(SNIS_BOA_0003,  dsOutUnColList.GetRowCount());
	    }
	}

	//오류가 없이 정상 처리되었다면...
	fcPostProcess(sServiceName);
}

/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 Post Process!
+-------------------------------------------------------------------------------------------------*/
function fcPostProcess(sServiceName) {

}

