	fce_AddCodeRow(dsOutCollCd, '전체', '', '');
	cboCollCd.Value = '';	
}

function cboStndYear_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{

}



function dsOutUnColList_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	if(strColumnID =="COLL_AMT"){
		var real = dsOutUnColList.GetColumn(nRow, "REAL_SUPP_AMT");
		var coll = dsOutUnColList.GetColumn(nRow, "COLL_AMT");
		var remain = (-real) -coll;
		var remain_r =0;

		if(varNewValue==0 ||varNewValue =='' || varNewValue== null){
			dsOutUnColList.SetColumn(nRow,"REMAIN_AMT", null);
			dsOutUnColList.SetColumn(nRow,"COLL_DT",null);	
			dsOutUnColList.SetColumn(nRow,"COLL_TPE",'001');		
		}else  if(remain < 0){
			//alert("수금액은 미수금보다 클 수가 없습니다.");
			fnc_SetStatus(SNIS_BOA_G012);		
			remain_r = remain;
			dsOutUnColList.SetColumn(nRow,"COLL_AMT",varOldValue);	
			return false;
		}else{		
			dsOutUnColList.SetColumn(nRow,"REMAIN_AMT",-remain);
			dsOutUnColList.SetColumn(nRow,"COLL_DT",CURR_DT);		
			if(remain > 0){
				dsOutUnColList.SetColumn(nRow,"COLL_TPE",'001');		
			}else if(remain == 0){
				dsOutUnColList.SetColumn(nRow,"COLL_TPE",'002');		
			}			
		}		

	}
}


//미수금 일괄 수금 
function btnBatchCollectMoney_OnClick(obj)
{
	if ( fnc_Message(SNIS_BOA_G006, "Y" ) ){
			for(i=0; i<dsOutUnColList.GetRowCount(); i++){
				if(dsOutUnColList.GetColumn(i,'COLL_TPE')=='001'){ //미수금이면 
					var colAmt = ToInteger(dsOutUnColList.GetColumn(i, 'REAL_SUPP_AMT'))* (-1);
					dsOutUnColList.SetColumn(i,"COLL_AMT", colAmt);
					dsOutUnColList.SetColumn(i,"COLL_DT",CURR_DT);	
					dsOutUnColList.SetColumn(i,"COLL_TPE",'002');		
				}
			}
	}
}
