//업체  조회 
function fcSearchPartsCom(){
	fce_AddParam("AWARD_YEAR", cboAwardYear.Value);
	fce_AddParam("AWARD_MONTH", cboAwardMonth.Value);
    fce_AddParam("RACER_NM", edRacerNm.Text);

    var sServiceName = "e07040020-service:searchAwardList";
    var sInDataSet   = "";
    var sOutDataSet  = "dsOutPartsCom=dsOutPartsCom";
    fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}
