/*-------------------------------------------------------------------------------------------------+
>>  저장 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSaveCheck(obj) {

	//변경된 자료가 있는지 체크
	if ( !fnc_DatasetChangeCheck(dsOutPartsCom )  )
	{
		//저장 할 자료가 없음
		fnc_Message(SNIS_COM_1006, "N");	
		return false;
	}

	// 필수항목체크
	oDataSet          = dsOutPartsCom;
	sMandatoryColID   = "RACER_NO|RACER_NM|AWARD_DT";
	sMandatoryColName = "선수번호|성명|포상일자";
	oGrid             = grdPartsCom;
	if ( fnc_GetMandatoryViolateRow(oDataSet, sMandatoryColID, sMandatoryColName, oGrid) > -1 ) {
	    return false;
	}

	return true;
}

function fcSave(obj) {
    if ( !fcSaveCheck() ) return;
    fce_AddParam("AWARD_YM", mskReprStrDt.Value);
    fce_AddParam("RACER_NM", edRacerNm.Text);

    var sServiceName = "e07040020-service:saveAwardDesc";
    var sInDataSet   = "dsOutPartsCom=dsOutPartsCom:u";
    var sOutDataSet  = "dsOutPartsCom=dsOutPartsCom";

   	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack",false);
}

/*-------------------------------------------------------------------------------------------------+
