function fcToExcel(obj) {
	fnc_OpenExcelExport("grdPartsCom");
}


/*-------------------------------------------------------------------------------------------------+
>>  행추가 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcAddRow(obj) {
	dsOutPartsCom.AddRow();  
	for(var i = 1 ; i <= dsOutPartsCom.GetRowCount(); i++) {
		dsOutPartsCom.SetColumn(dsOutPartsCom.rowposition,"NUM",i);
	}	
}

/*-------------------------------------------------------------------------------------------------+
>>  행삭제 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcDelRow(obj) {
	if(dsOutPartsCom.GetRowCount()==0){
		fnc_SetStatus(SNIS_COM_1003,  dsOutPartsCom.GetRowCount());		
	}

	dsOutPartsCom.DeleteRow(dsOutPartsCom.Row);

	for(var i = 1 ; i <= dsOutPartsCom.GetRowCount(); i++) {
		dsOutPartsCom.SetColumn(dsOutPartsCom.rowposition,"NUM",i);
	}		
}

