}

function grdPartsCom_OnCellClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	if(nCell ==1 || nCell ==3){
		if(dsOutPartsCom.GetColumn(dsOutPartsCom.rowposition, "RACER_NO") ==null){
			 // Content로 넘겨지는 arguements
			var sArg = "Arg_sRacerNm=" ;
			var sResponse = Dialog("form::E07040021.xml", sArg, 834, 450, "");

			if ( !fnc_IsNull(sResponse) ) {
				dsOutPartsCom.SetColumn(nRow, "RACER_NO"  , sResponse[0]);
				dsOutPartsCom.SetColumn(nRow, "RACER_NM"  , sResponse[1]);
			}
		}
		else{		   
	    }

	}
}
