	btnConfirmInsuPen.enable = false;
	divBtnLst.btnDelete.enable = false;

	// 경륜정보 Combo 조회
	//fcd_GetRaceInfo(sFormId, cboStndYear, cboMeetCd, cboTms, null, null, false);

    //divRacerSearch.url = DIV_CODE_URL;

    //SetTimer(1, 100);

    // 시스템 날짜 조회 
    //sCurDate = fcd_GetSysdate("YYYYMMDD");
    //sCurYear = substr(sCurDate, 0, 4);  

    fcSearchPayDt(this);
    fcSearch();
}


/*-------------------------------------------------------------------------------------------------+
