/*-------------------------------------------------------------------------------------------------+
>>  취소 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcInit(obj) {
    dsList.ClearData();
    edtPayDt.value = "";

}

/*-------------------------------------------------------------------------------------------------+
>> 지급일 조회
+-------------------------------------------------------------------------------------------------*/
function fcSearchPayDt(obj) {

	var sServiceName = "e07050020-service:searchDayInfo";
	var sInDataSet   = "";
	var sOutDataSet  = "dsDayInfo=dsDayInfo dsYear=dsYear ";

	http.Sync = true;
	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
	http.Sync = false;

}

/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearchCheck(obj) {
	fcInit(obj);	
	return true;
}

function fcSearch(obj) {

    if ( !fcSearchCheck(obj) ) return;

	var sServiceName = "e07050020-service:searchList";
    var sInDataSet   = "";
    var sOutDataSet  = "dsList=dsList ";

	fce_AddParam("PAY_DT", dsDayInfo.GetColumn(cboPayDt.Index, "PAY_DT"));

	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);
}

/*-------------------------------------------------------------------------------------------------+
>>  저장 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/

function fcSaveCheck(obj) {   	


	//변경된 자료가 있는지 체크
	if ( !fnc_DatasetChangeCheck(dsList)) {
		//저장 할 자료가 없음
		fnc_Message(SNIS_COM_1006, "N");	
		return false;
	}


	// 필수항목체크
	oDataSet          = dsList;
	sMandatoryColID   = "RACER_NO|RACER_NM|ENGM_NM|ENGM_RES_NO|INSU_CD|INSU_NO|INSU_AMT|ENGM_YN|REG_DT";
	sMandatoryColName = "선수번호|선수명|계약자명|주민등록번호|보험사|가입증서번호|보험료|계약상태|등록일";
	oGrid             = grdList;
	if ( fnc_GetMandatoryViolateRow(oDataSet, sMandatoryColID, sMandatoryColName, oGrid) > -1 ) {
	    return false;
	}		


	return true;
}

function fcSave(obj) {
    if ( !fcSaveCheck() ) return;    

    var confirmMsg = "저장하시겠습니까?";
    if(fnc_Message(L_COM_CNF_0000, "Y", confirmMsg)) {           
		var sServiceName = "";
		var sInDataSet   = "";
		var sOutDataSet  = "";    

		sServiceName = "e07050020-service:saveInsDetlList";
		sInDataSet   = "dsList=dsList:u ";

		sOutDataSet  = "";	

		fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);
    }
}


function fcCreateCheck(obj) {   
	if (edtPayDt.value.length != 8 ) {
		msg = "지급일을 입력하세요!";
		fnc_Message(L_COM_CNF_0000, "N", msg);
		return false;
	} else if (dsDayInfo.FindRow("PAY_DT",edtPayDt.value,0) >= 0) {
		msg = "해당 지급일에 데이터가 존재합니다!";
		fnc_Message(L_COM_CNF_0000, "N", msg);
		return false;
	}
	else return true;
}

function fcCreate(obj) {
	if ( !fcCreateCheck() ) return;    

    var confirmMsg = "선수연금보험 데이터를 생성하시겠습니까?";
    if(fnc_Message(L_COM_CNF_0000, "Y", confirmMsg)) {           
		var sServiceName = "";
		var sInDataSet   = "";
		var sOutDataSet  = "";    

		sServiceName = "e07050020-service:saveInsDetlList";
		sInDataSet   = "dsList=dsList:a ";

		sOutDataSet  = "";				

		fce_AddParam("JOBTYPE", "MAKE");
		fce_AddParam("PAY_DT", edtPayDt.Value);

		fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);
    }
}

function fcDeleteCheck(obj) {
	// 지출확정된 자료는 삭제할 수 없다.
	if (dsList.GetColumn(0, "PAY_STAT_CD") != "001") {
		fnc_message("지출확정된 자료는 삭제할 수 없습니다.", "N");
		return false;
	}
	return true;
}

/*-------------------------------------------------------------------------------------------------+
>>
+-------------------------------------------------------------------------------------------------*/
function fcDelete(obj) {
	if (!fcDeleteCheck()) return;

	var msg = "지급일: "+dsDayInfo.GetColumn(cboPayDt.Index, "PAY_DT");

	if(fnc_Message(SNIS_COM_1004, "Y", msg)) {
		var sServiceName = "";
		var sInDataSet   = "";
		var sOutDataSet  = "";

		sServiceName = "e07050020-service:saveInsDetlList";
		sInDataSet   = "";
		sOutDataSet  = "";				

		fce_AddParam("JOBTYPE", "DELETE");
		fce_AddParam("PAY_DT", dsDayInfo.GetColumn(cboPayDt.Index, "PAY_DT"));

		fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);
	}
}


function fcConfirmCheck(obj) {
	return true;
}

/*-------------------------------------------------------------------------------------------------+
>>
+-------------------------------------------------------------------------------------------------*/
function fcConfirm(obj) {
	if (!fcConfirmCheck()) return;

	var msg = "지급일: "+dsDayInfo.GetColumn(cboPayDt.Index, "PAY_DT");

	if(fnc_Message(SNIS_BOA_G016, "Y", msg)) {
		var sServiceName = "";
		var sInDataSet   = "";
		var sOutDataSet  = "";

		sServiceName = "e07050020-service:saveInsDetlList";
		sInDataSet   = "";
		sOutDataSet  = "";				

		fce_AddParam("JOBTYPE", "CONFIRM");
		fce_AddParam("PAY_DT", dsDayInfo.GetColumn(cboPayDt.Index, "PAY_DT"));

		fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);
	}
}

