/***************************************************************************************************
*                                      사용자 정의  처리 부분                                      *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {

	if ( !fnc_result(ErrorCode, ErrorMSG) ) return;

	// 조회 완료 후
	if ( sServiceName == "e07050020-service:searchDayInfo" ) {
		cboStndYear.Index = iif(dsYear.GetRowCount() > 0, 0, -1);
		dsDayInfo.Filter("STND_YEAR="+cboStndYear.Value); //해당 년도만 표시
		cboPayDt.Index = iif(dsDayInfo.GetRowCount() > 0, 0, -1);

		//SetTimer(1, 100);
		//fcSearch();
		break;
	}

	// 조회 완료 후
	if ( sServiceName == "e07050020-service:searchList" ) {
		//dsCanRecreate=dsCanRecreate dsEduAidRegiCnt=dsEduAidRegiCnt 

	}

	// 생성/삭제/확정 완료 후
	if ( sServiceName == "e07050020-service:saveInsDetlList" ) {
		fcSearchPayDt();
		fcSearch();
	}

	if(dsList.getColumn(0,'PAY_STAT_CD') == '004')	{ //지급상태: 지출확정 이면 
		btnConfirmInsuPen.enable = false;
		divBtnLst.btnDelete.enable = false;
	} else {
		btnConfirmInsuPen.enable = true;
		divBtnLst.btnDelete.enable = true;
	}	

}


