/*-------------------------------------------------------------------------------------------------+
>>  null -> ""
+-------------------------------------------------------------------------------------------------*/	
function n2s(str) {
	if(str == null) return "";
	return str;
} 	

function nvl(val, retval) {
	if(val == null) return retval;
	return val;
} 	


function fcCboOnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	switch (obj.Id) {
		case "cboStndYear" : // 년도 변경
			dsDayInfo.Filter("STND_YEAR="+cboStndYear.Value); //해당 년도만 표시
			cboPayDt.Index = iif(dsDayInfo.GetRowCount() > 0, 0, -1);
			fcSearch(this);
			break;

		case "cboPayDt" : // 지급일
			//dsMast.Row = cboTermDt.Index; // innerDataSet의 row는 콤보가 변경된다고 해서 변경되는것이 아니다.
			fcSearch(this);
			break;

		default :
			break;
	}
}
