/*-------------------------------------------------------------------------------------------------+
>>  최초 화면 Load時 처리 할 사항
+-------------------------------------------------------------------------------------------------*/
function fcFormLoadSetting(obj) {  
    sBUTTONLIST   = 'F'+sInpt+'FFFT';
 	//화면환경 설정
	sFORMCAPTION  = obj.Title;
	sFORMLOCATION = "";

	//화면 정보 Display 및 Button 권한 설정
	fnc_FormLoadSetting(obj, sFORMCAPTION, sFORMLOCATION, sBUTTONLIST, sUSERAUTH);

	//초기 화면 BK Color설정 
	fnc_SetBKColor(this);

	fce_SetStndYear(dsOutStndYear, toInteger(fce_GetSysdate("YYYY")));

    cboStndYear.Index = 0;

    fcComSeq();

}
/*-------------------------------------------------------------------------------------------------+
>>  최초 화면 UNLoad時 처리 할 사항
+-------------------------------------------------------------------------------------------------*/
function fcFormUnloadProcess(obj) {
	//자료 변경 여부를 체크하고자 하는  DataSet Object를 넘긴다.
	var sDataset = "";
	if ( !fnc_FormUnloadCheck(sDataset, sFormId) ) return false;
}
/*-------------------------------------------------------------------------------------------------+
>>  단축키 처리(Form KeyDown)
+-------------------------------------------------------------------------------------------------*/
function fcHotKey(obj,objSenderObj,nChar,bShift,bControl,bAlt,nLLParam,nHLParam) {

	//Return Key Press時 Next Component focus
	if ( nChar == 13 ) {
		var oFocusComponent = GetNextComponent(true);
		oFocusComponent.setfocus();
		return;
	}
}
/*-------------------------------------------------------------------------------------------------+
>>  엑셀 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcToExcel(obj) {
    var sExcelList = "";
    fce_OpenExcelListExport(sExcelList);
}


/*-------------------------------------------------------------------------------------------------+
>>  화면 종료(닫기) 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcEnd(obj) {
	//화면 종료
	Close();	
}
function fcComSeq(){
	var sServiceName = "e08010007-service:searchSimulationSeq";
    var sInDataSet   = "";
    var sOutDataSet  =  " dsSeq=dsSeq ";

	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack",false);
}
function fcSearch(){

	var sServiceName = "e08010007-service:searchSimulationList";
    var sInDataSet   = "";
    var sOutDataSet  =  " dsSimulList=dsSimulList ";

    fce_AddParam("STND_YEAR", cboStndYear.Value);
    fce_AddParam("SEQ"      , cmbSeq.Value); 
	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");
}
function fcSaveCheck(){
    if(txtComments.Text ==""){
		fnc_Message("변경사유를 등록해 주십시오!", "N");
		txtComments.SetFocus();
		return false;
    }
    if(txtComments.Text == txtcheck.Text){
		fnc_Message("변경사유를 수정해 주십시오!", "N");
		txtComments.SetFocus();
		return false;
    }
    for (var i = 0; i < dsSimulList.RowCount();i++){
		if(dsSimulList.GetColumn(i,"NEW_RATE") == 0){
		   fnc_Message(" 등록되지 않은 비중이 있습니다.\n 확인바랍니다.", "N");
		   return false;
		}
    }
    return true;
}
function fcSave(){

	if ( !fcSaveCheck() ) return;

	for (var i = 0; i < dsSimulList.RowCount();i++){
		dsSimulList.SetColumn(i,"COMMENTS",txtComments.Text);
    }
	var sServiceName = "e08010007-service:saveSimulationRate";
    var sInDataSet   = " dsSimulList=dsSimulList:u ";
    var sOutDataSet  = "";
   // fnc_DatasetPrint(dsSimulList);
    fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");
}
/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {

	if ( !fnc_result(ErrorCode, ErrorMSG) ) return;
	if ( sServiceName == "e08010007-service:searchSimulationSeq" ) {
	    cmbSeq.Index = 0;
	    txtComments.text = dsSeq.GetColumn(0,"COMMENTS");
	    txtcheck.Text = txtComments.text ;
	    fcSearch();
		return;
	}
	if ( sServiceName == "e08010007-service:searchSimulationList" ) {
        return;
	}
	if ( sServiceName == "e08010007-service:saveSimulationRate" ) {
		fcComSeq();
        return;
	}
}
function dsSimulList_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
    var new_point          = 0;
    var rate_declination   = 0;
    var point_declination  = 0;

	switch(strColumnID){
		case  "NEW_RATE" : 
							 new_point 		   = (dsSimulList.GetColumn(nRow,"NEW_RATE") * dsSimulList.GetColumn(nRow,"ORG_POINT")) / 100;
							 rate_declination  = dsSimulList.GetColumn(nRow,"NEW_RATE") - dsSimulList.GetColumn(nRow,"RATE");
							 dsSimulList.SetColumn(nRow,"NEW_POINT",ROUND(new_point,2));
							 dsSimulList.SetColumn(nRow,"RATE_DECLINATION" ,ROUND(rate_declination,2) );

							 point_declination = dsSimulList.GetColumn(nRow,"NEW_POINT") - dsSimulList.GetColumn(nRow,"POINT");
							 dsSimulList.SetColumn(nRow,"POINT_DECLINATION",ROUND(point_declination,2));
			      break;						   
		default : break;
	}
}

function cmbSeq_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	txtComments.text = dsSeq.GetColumn(nNewIndex,"COMMENTS");
	txtcheck.Text = txtComments.text ;
	fcSearch();
}

function grdSimulList_OnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	if (nCell == 2){
	    var sCd_nm = dsSimulList.GetColumn(nRow, "CD_NM");
	    var sContents = dsSimulList.GetColumn(nRow, "CONTENTS");
	    var sArg = "Arg_sCd_nm=" + quote(sCd_nm) + " Arg_Cont=" + quote(sContents);
		Dialog("form::E08010004_1.xml", sArg, 0, 0, "");
	}
}
