function fcInit(obj) {
   dsOutRacer.ClearData();
   dsOutRacerViol.ClearData();
   dsRaceViolAnalysis.ClearData();
}
/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearchCheck(obj) {

	fcInit(obj);

	if(fnc_isNull(cboSStndYear.Value)) {
            fnc_Message(SNIS_COM_1034, "N", "년도");
            cboSStndYear.SetFocus();
            return false;
    }
    if(fnc_isNull(cboSMbr.Value)) {
            fnc_Message(SNIS_COM_1034, "N", "경정장");
            cboSMbr.SetFocus();
            return false;
    } 
    if(fnc_isNull(cboSRaceTms.Value)) {
            fnc_Message(SNIS_COM_1034, "N", "회차");
            cboSRaceTms.SetFocus();
            return false;
    }
    if(fnc_isNull(cboSDayOrd.Value)) {
            fnc_Message(SNIS_COM_1034, "N", "일차");
            cboSDayOrd.SetFocus();
            return false;
    } 
	if(fnc_isNull(cboSRaceNo.Value)) {
            fnc_Message(SNIS_COM_1034, "N", "경주번호");
            cboSRaceNo.SetFocus();
            return false;
    }

	return true;
}

function fcSearch(obj) {
    if ( !fcSearchCheck(obj) ) return;
    //선수별요약정보조회 
	var sServiceName = "e09020001-service:searchRaceRacer";
    var sInDataSet   = "";
    var sOutDataSet  = "dsOutRacer=dsOutRacer dsRaceViolAnalysis=dsRaceViolAnalysis ";
    fce_AddParam(Trim("STND_YEAR"), cboSStndYear.Value);
	fce_AddParam(Trim("MBR_CD   "), cboSMbr.Value     );
	fce_AddParam(Trim("TMS      "), cboSRaceTms.Value );
	fce_AddParam(Trim("DAY_ORD  "), cboSDayOrd.Value  );
	fce_AddParam(Trim("RACE_NO  "), cboSRaceNo.Value  );
    fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}

function fcSearchRacerViol(obj, nRow) {
    dsOutRacerViol.ClearData();
	//선수별사고정보조회
	var sServiceName = "e09020001-service:searchRacerViol";
    var sInDataSet   = "";
    var sOutDataSet  = "dsOutRacerViol=dsOutRacerViol ";
    fce_AddParam(Trim("STND_YEAR"), dsOutRacer.getColumn(nRow, "STND_YEAR"));
	fce_AddParam(Trim("MBR_CD   "), dsOutRacer.getColumn(nRow, "MBR_CD"));
	fce_AddParam(Trim("TMS      "), dsOutRacer.getColumn(nRow, "TMS"));
	fce_AddParam(Trim("DAY_ORD  "), dsOutRacer.getColumn(nRow, "DAY_ORD"));
	fce_AddParam(Trim("RACE_NO  "), dsOutRacer.getColumn(nRow, "RACE_NO"));
	fce_AddParam(Trim("RACER_NO "), dsOutRacer.getColumn(nRow, "RACER_NO"));
    fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false); 
}

/*-------------------------------------------------------------------------------------------------+
>>  저장 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSaveCheck(obj) {

	//변경된 자료가 있는지 체크
	if ( !fnc_DatasetChangeCheck(dsOutRacer)
	  && !fnc_DatasetChangeCheck(dsOutRacerViol)
	  && !fnc_DatasetChangeCheck(dsRaceViolAnalysis)
	) {
	//저장 할 자료가 없음
		fnc_Message(SNIS_COM_1006, "N");	
		return false;
	}

	return true;
}

function fcSave(obj) {

	if ( !fcSaveCheck() ) return;

	var ServiceName = "e09020001-service:saveAcctEduRacer";
    var sInDataSet  = "dsOutRacer=dsOutRacer:u dsOutRacerViol=dsOutRacerViol:u dsRaceViolAnalysis=dsRaceViolAnalysis:u ";
    var sOutDataSet = "dsOutRacer=dsOutRacer";

 	fce_SendTransaction(ServiceName, sInDataSet, sOutDataSet, "fcCallBack"); 
}

