function fcInit(obj) {
   dsOutEduCont.ClearData();
}
/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearchCheck(obj) {

	fcInit(obj);

	if(fnc_isNull(cboSStndYear.Value)) {
            fnc_Message(SNIS_COM_1034, "N", "년도");
            cboSStndYear.SetFocus();
            return false;
    }
    if(fnc_isNull(cboSMbr.Value)) {
            fnc_Message(SNIS_COM_1034, "N", "경정장");
            cboSMbr.SetFocus();
            return false;
    } 
    if(fnc_isNull(cboSRaceTms.Value)) {
            fnc_Message(SNIS_COM_1034, "N", "시작회차");
            cboSRaceTms.SetFocus();
            return false;
    }
    if(fnc_isNull(cboERaceTms.Value)) {
            fnc_Message(SNIS_COM_1034, "N", "종료회차");
            cboERaceTms.SetFocus();
            return false;
    }
    if(fnc_isNull(cboSDayOrd.Value)) {
            fnc_Message(SNIS_COM_1034, "N", "일차");
            cboSDayOrd.SetFocus();
            return false;
    } 
	return true;
}

function fcSearch(obj) {
    if ( !fcSearchCheck(obj) ) return;
    //선수별요약정보조회 
	var sServiceName = "e09030001-service:searchEduCont";
    var sInDataSet   = "";
    var sOutDataSet  = "dsOutEduCont=dsOutEduCont ";
    fce_AddParam(Trim("STND_YEAR"), cboSStndYear.Value);
	fce_AddParam(Trim("MBR_CD   "), cboSMbr.Value     );
	fce_AddParam(Trim("STMS      "), cboSRaceTms.Value );
	fce_AddParam(Trim("ETMS      "), cboERaceTms.Value );
	fce_AddParam(Trim("DAY_ORD  "), cboSDayOrd.Value  );
    fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}

/*-------------------------------------------------------------------------------------------------+
>>  저장 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSaveCheck(obj) {

	//변경된 자료가 있는지 체크
	if ( !fnc_DatasetChangeCheck(dsOutEduCont)) {
	//저장 할 자료가 없음
		fnc_Message(SNIS_COM_1006, "N");	
		return false;
	}
	return true;
}

function fcSave(obj) {

	if ( !fcSaveCheck() ) return;

	var ServiceName = "e09030001-service:saveEduCont";
    var sInDataSet  = "dsOutEduCont=dsOutEduCont:u ";
    var sOutDataSet = "dsOutEduCont=dsOutEduCont";

 	fce_SendTransaction(ServiceName, sInDataSet, sOutDataSet, "fcCallBack"); 
}

