/*-------------------------------------------------------------------------------------------------+
>>  회차 변경시
+-------------------------------------------------------------------------------------------------*/
function cboSRaceTms_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
    var nIndexRow = fnc_GetExistValue(dsOutSRaceTms, "TMS", cboSRaceTms.Value);
	calStrDt.Value = dsOutSRaceTms.GetColumn(nIndexRow, "STR_DT");
}

/*-------------------------------------------------------------------------------------------------+
>>  회차변경시
+-------------------------------------------------------------------------------------------------*/
function cboERaceTms_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
    var nIndexRow = fnc_GetExistValue(dsOutERaceTms, "TMS", cboERaceTms.Value);
    calEndDt.Value = dsOutERaceTms.GetColumn(0, "END_DT");
}

