function fcBatchApply(obj)
{
	var i;
	var tmpEdu_DT;
	var tmpEdu_Contents;

	tmpEdu_Dt = dsOutEduCont.GetColumn(dsOutEduCont.currow , "EDU_DT");
	tmpEdu_Contents = dsOutEduCont.GetColumn(dsOutEduCont.currow , "EDU_CONT");

	for(i=0;i<dsOutEduCont.RowCount();i++) {
		if(dsOutEduCont.GetColumn(i,"chk") =="1") {
			dsOutEduCont.SetColumn(i,"EDU_DT",tmpEdu_Dt);
			dsOutEduCont.SetColumn(i,"EDU_CONT",tmpEdu_Contents);
		}
	}
	grdOutEduCont.ReDraw();
}
