/*-------------------------------------------------------------------------------------------------+
>>  Clear 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcInit(obj) {
	edSEmpNm.Text        = "";
	edSDeptCd.Text       = "";
	edSDeptNm.Text       = "";
	rdoSLucalSocal.Index = 0;
	cboSRelgnCd.Index    = 0;
	calSBirthDt.Value    = "";

    dsOutEmp.ClearData();
    edSEmpNm.SetFocus();
}

/*-------------------------------------------------------------------------------------------------+
>>  조회 전 Check
+-------------------------------------------------------------------------------------------------*/
function fcSearchCheck() {
	return true;
}

/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearch(obj) {
    if ( !fcSearchCheck() ) return;

	var sServiceName = "zasmp01-service:search";
    var sInDataSet   = "";
    var sOutDataSet  = "dsOutEmp=ds_emp";

	fce_AddParam(Trim("EMP_NM         "), edSEmpNm.Text       );
	fce_AddParam(Trim("DEPT_CD        "), edSDeptCd.Text      );
	fce_AddParam(Trim("LUCAL_SOCAL_TPE"), rdoSLucalSocal.Value);
	fce_AddParam(Trim("RELGN_CD       "), cboSRelgnCd.Value   );
	fce_AddParam(Trim("BIRTH_DT       "), calSBirthDt.Value   );

	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");
}
/*-------------------------------------------------------------------------------------------------+
>>  저장 전 Check
+-------------------------------------------------------------------------------------------------*/
function fcSaveCheck(obj) {

	//변경된 자료가 있는지 체크
	if ( !fnc_DatasetChangeCheck(dsOutEmp) ) {
		//저장 할 자료가 없음
		fnc_Message(SNIS_COM_1006, "N");	
		return false;
	}

	// 필수항목체크
	oDataSet          = dsOutEmp;
	sMandatoryColID   = "EMP_NM||DEPT_CD||RES_NO||LUCAL_SOCAL_TPE||RELGN_CD||BIRTH_DT";
	sMandatoryColName = "사원명||부서||주민번호||음양력구분||종교||생년월일";
	if ( fnc_GetMandatoryViolateRow(oDataSet, sMandatoryColID, sMandatoryColName, grdMaster) > -1 ) {
	    return false;
	}

    // 주민등록번호 Check
    var sColumnID = "RES_NO";
    for ( i = 0; i < oDataSet.GetRowCount(); i++ ) {
        if ( oDataSet.GetRowType(i) != "normal" ) {
			// 필드값에 값이 없는 경우 메세지를 호출 한 후 해당 Row번호를 return 한다.
			if ( !fnc_ResidenceNumberCheck(oDataSet.GetColumn(i, sColumnID)) ) {
				fnc_Message(SNIS_BOA_0002, "N", i + 1, "주민번호");
				oDataSet.row = i;
			    grdMaster.SetFocus();
				grdMaster.SetCellPos(grdMaster.GetBindCellIndex("body", sColumnID));
				return false;
			}
        }
    }

    // 파일 업로드
    for ( i = 0; i < oDataSet.GetRowCount(); i++ ) {
        if ( oDataSet.GetColumn(i, "PTO_UPDATE") == "Y" ) {
			var rtn_arr = fnc_FileWrite(divBtnLst.HttpFile, oDataSet.GetColumn(i, "PTO_PATH"), 
															oDataSet.GetColumn(i, "PTO_NM"));

			if ( rtn_arr[0] != "SUCC" ) {
				alert("파일 업로드 에러발생");
				return false;
			}

			oDataSet.SetColumn(i, "PTO_PATH", rtn_arr[1]);
        }
    }

	return true;
}

/*-------------------------------------------------------------------------------------------------+
>>  저장 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSave(obj) {

    if ( !fcSaveCheck() ) return;

	var sServiceName = "zasmp01-service:save";
    var sInDataSet   = "input=dsOutEmp:u";
    var sOutDataSet  = "";

	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");
}

