/*-------------------------------------------------------------------------------------------------+
>>  행추가 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcAddRow(obj) {

	//DataSet의 Header 정보가 있을 때  Add 처리 
	if (dsOutEmp.ColCount() > 0) {
		dsOutEmp.AddRow();                    //Record Add
		grdMaster.SetFocus();                 //입력 첫 항목으로 Focus
	} else {
		fnc_Message(SNIS_COM_0002, "N");      //DataSet의 Header가 없어서 오류
	}
}

/*-------------------------------------------------------------------------------------------------+
>>  행삭제 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcDelRow(obj) {
	//삭제 할 자료가 있는지 여부를 체크
	if ( dsOutEmp.RowCount() > 0 ) {
		if ( fnc_DeleteCheck(dsOutEmp, "EMP_NM") ) dsOutEmp.DeleteSelected();        //삭제 여부 확인
	} else {
		fnc_Message(SNIS_COM_1003, "N");                      //삭제 자료 없음 정보 Display
	}

	grdMaster.SetFocus();                                     //Record 삭제 후 Grid로 Focus
}

