SELECT /* rsm2060_s01 */ 0 AS ORD ,MEET_CD -- 경주구분 코드 ,TDIV_NO , --지점 코드 NO_ORDER, -- 정렬 순서 DECODE(NO_ORDER,'B71','광복','B72','서면','B73','금정','B74','김해','B75','창원본장','A99','광명', BB.CD_NM) AS BRNCH_NM, -- 지점 명 NVL(N_DIV_TOTAL,0) N_DIV_TOTAL, -- 금회 금액 DECODE(N_DIV_TOTAL,0,0,ROUND(N_DIV_TOTAL/SUM(N_DIV_TOTAL) OVER()*100,1)) AS DIV_TOTAL_RATE, -- 구성비 NVL(B_DIV_TOTAL,0) B_DIV_TOTAL -- 전회 매출 ,CASE WHEN B_DIV_TOTAL > N_DIV_TOTAL THEN '△' --감소 WHEN B_DIV_TOTAL = N_DIV_TOTAL THEN '-' --같음 WHEN B_DIV_TOTAL < N_DIV_TOTAL THEN '' --증가 END B_DIV_TOTAL_SIGN -- 증감율 사인 --,DECODE(B_DIV_TOTAL,0,0,ROUND(ABS(B_DIV_TOTAL-N_DIV_TOTAL)/B_DIV_TOTAL*100,1)) B_DIV_TOTAL_RATE -- 증감율 ,DECODE(B_DIV_TOTAL,0,0,ROUND(ABS(round(B_DIV_TOTAL,0)-round(N_DIV_TOTAL,0))/B_DIV_TOTAL*100,1)) B_DIV_TOTAL_RATE -- 증감율 ,L_DIV_TOTAL -- 전년 동회대비 금액 ,CASE WHEN L_DIV_TOTAL > N_DIV_TOTAL THEN '△' --감소 WHEN L_DIV_TOTAL = N_DIV_TOTAL THEN '-' --같음 WHEN L_DIV_TOTAL < N_DIV_TOTAL THEN '' --증가 END L_DIV_TOTAL_SIGN -- 전년 동회대비 사인 ,CASE WHEN L_DIV_TOTAL<1 THEN 0 ELSE ROUND(ABS(L_DIV_TOTAL-N_DIV_TOTAL)/L_DIV_TOTAL*100,1) END L_DIV_TOTAL_RATE -- 전년 동회대비 증감율 ,ROUND(N_DIV_TOTAL*7.5/100,0) COMMI FROM ( SELECT MEET_CD ,TDIV_NO ,SUM(CASE WHEN MEET_CD = '004' AND TDIV_NO = '900' THEN 0 ELSE N_DIV_TOTAL END)/1000000 N_DIV_TOTAL ,ROUND(SUM(CASE WHEN MEET_CD = '004' AND TDIV_NO = '900' THEN 0 ELSE B_DIV_TOTAL END)/1000000,0) B_DIV_TOTAL ,SUM(CASE WHEN MEET_CD = '004' AND TDIV_NO = '900' THEN 0 ELSE L_DIV_TOTAL END)/1000000 L_DIV_TOTAL, CASE WHEN TDIV_NO ='071' THEN 'B71' WHEN TDIV_NO ='072' THEN 'B72' WHEN TDIV_NO ='073' THEN 'B73' WHEN TDIV_NO ='074' THEN 'B74' WHEN TDIV_NO ='075' THEN 'B75' ELSE 'A'||TDIV_NO END AS NO_ORDER FROM ( SELECT MEET_CD, SELL_CD, COMM_NO, DIV_NO, CASE WHEN SELL_CD= '03' THEN '01' --본장9미사리,그린카드포함) WHEN SELL_CD = '01' AND COMM_NO = '12' THEN '12' --상봉 WHEN SELL_CD = '01' AND COMM_NO = '13' THEN '13' --일산 WHEN SELL_CD = '01' AND COMM_NO = '14' THEN '14' --분당 WHEN SELL_CD = '01' AND COMM_NO = '15' THEN '15' --동대문 WHEN SELL_CD = '01' AND COMM_NO = '16' THEN '16' --장안 WHEN SELL_CD = '01' AND COMM_NO = '19' THEN '19' --관악 WHEN SELL_CD = '01' AND COMM_NO = '20' THEN '20' --길음 WHEN SELL_CD = '01' AND COMM_NO = '22' THEN '22' --유성 WHEN SELL_CD = '01' AND COMM_NO = '23' THEN '23' --인천 WHEN SELL_CD = '01' AND COMM_NO = '24' THEN '24' --시흥 WHEN SELL_CD = '01' AND COMM_NO = '25' THEN '25' --논현(+마이켓) WHEN SELL_CD = '01' AND COMM_NO = '26' THEN '26' --천안 WHEN SELL_CD = '01' AND COMM_NO = '27' THEN '27' --올림픽공원(+마이켓) WHEN SELL_CD = '01' AND COMM_NO = '28' THEN '28' --의정부 WHEN SELL_CD = '04' AND COMM_NO='07' AND DIV_NO='01' THEN '071' -- 광복 WHEN SELL_CD = '04' AND COMM_NO='07' AND DIV_NO='02' THEN '072' -- 서면 WHEN SELL_CD = '04' AND COMM_NO='07' AND DIV_NO='03' THEN '073' -- 부산본장 WHEN SELL_CD = '02' AND COMM_NO='07' AND DIV_NO='01' THEN '075' -- 창원본장 WHEN SELL_CD = '02' AND COMM_NO='07' AND DIV_NO='02' THEN '074' -- 김해 WHEN SELL_CD = '01' THEN '99' -- 광명 ELSE '999' END TDIV_NO, SUM(N_DIV_TOTAL) N_DIV_TOTAL, SUM(B_DIV_TOTAL) B_DIV_TOTAL, SUM(L_DIV_TOTAL) L_DIV_TOTAL FROM ( SELECT A.MEET_CD, A.SELL_CD, A.COMM_NO, A.DIV_NO, A.DIV_TOTAL N_DIV_TOTAL, 0 B_DIV_TOTAL, 0 L_DIV_TOTAL FROM VW_SDL_DT_SUM_GSUM A, ( -->>>>>>>>>>>>>>>>현회차 SELECT STND_YEAR, MEET_CD, TMS, DAY_ORD, RACE_YOIL FROM VW_SDL_INFO WHERE 1=1 AND STND_YEAR = ? -- 0:STND_YEAR '2011' --*조건(기준년도) AND MEET_CD = '003' AND TMS = ? -- 1:TMS '10' --*조건(회차)
) B WHERE 1=1 AND A.STND_YEAR = B.STND_YEAR AND A.MEET_CD = B.MEET_CD AND A.TMS = B.TMS AND A.DAY_ORD = B.DAY_ORD UNION ALL SELECT A.MEET_CD, A.SELL_CD, A.COMM_NO, A.DIV_NO, 0 N_DIV_TOTAL, A.DIV_TOTAL B_DIV_TOTAL, 0 L_DIV_TOTAL FROM VW_SDL_DT_SUM_GSUM A, ( -->>>>>>>>>>>>>>>>지난회차 SELECT STND_YEAR, MEET_CD, TMS, DAY_ORD, RACE_YOIL FROM VW_SDL_INFO WHERE 1=1 AND STND_YEAR = ? -- 2:B_TMS_YEAR '2011' --*조건(전회차 기준년도) AND MEET_CD = '003' AND TMS = ? -- 3:B_TMS '10' --*조건(전회차) ) B WHERE 1=1 AND A.STND_YEAR = B.STND_YEAR AND A.MEET_CD = B.MEET_CD AND A.TMS = B.TMS AND A.DAY_ORD = B.DAY_ORD UNION ALL SELECT A.MEET_CD, A.SELL_CD, A.COMM_NO, A.DIV_NO, 0 N_DIV_TOTAL, 0 B_DIV_TOTAL, A.DIV_TOTAL L_DIV_TOTAL FROM VW_SDL_DT_SUM_GSUM A, ( -->>>>>>>>>>>>>>>>작년회차 SELECT STND_YEAR, MEET_CD, TMS, DAY_ORD, RACE_YOIL FROM VW_SDL_INFO WHERE 1=1 AND STND_YEAR = ? -- 4:B_STND_YEAR '2011'-1 --*조건(전년도) AND MEET_CD = '003' AND TMS = ? --5:TMS '10' --*조건(회차)
) B WHERE 1=1 AND A.STND_YEAR = B.STND_YEAR AND A.MEET_CD = B.MEET_CD AND A.TMS = B.TMS AND A.DAY_ORD = B.DAY_ORD
) GROUP BY MEET_CD, SELL_CD, COMM_NO, DIV_NO
UNION ALL SELECT '003' MEET_CD, '04 ' SELL_CD, '07' COMM_NO, '03' DIV_NO, '073' TDIV_NO, 0 N_DIV_TOTAL, 0 B_DIV_TOTAL, 0 L_DIV_TOTAL FROM DUAL UNION ALL SELECT '003' MEET_CD, '02 ' SELL_CD, '07' COMM_NO, '02' DIV_NO, '074' TDIV_NO, 0 N_DIV_TOTAL, 0 B_DIV_TOTAL, 0 L_DIV_TOTAL FROM DUAL UNION ALL SELECT '003' MEET_CD, '02 ' SELL_CD, '07' COMM_NO, '01' DIV_NO, '075' TDIV_NO, 0 N_DIV_TOTAL, 0 B_DIV_TOTAL, 0 L_DIV_TOTAL FROM DUAL ) GROUP BY MEET_CD, TDIV_NO -- ROLLUP(MEET_CD, TDIV_NO) )AA, ( -- 지점명 호출 하기 위한 공통 코드 조회 SELECT CD, DECODE(CD,'01','본장',CD_NM) AS CD_NM FROM TBRK_SPEC_CD A WHERE 1=1 AND A.GRP_CD = '060' AND A.CD NOT IN('00','29','06') )BB WHERE 1=1 AND AA.TDIV_NO= BB.CD(+) AND TDIV_NO <> '999' ORDER BY AA.NO_ORDER

SELECT /* rsm2060_s09 */ DU.LEV, AA.TMS AS B_TMS, -- 전년도 회차 BB.TMS AS TMS, -- 현년도 회차 AA.B_POOL_TOTAL, -- 전년도 합계 BB.POOL_TOTAL -- 현재 년도 합계 FROM ( -- 전년도 SELECT A.TMS, ROUND(SUM(A.POOL_TOTAL)/100000000,0) AS B_POOL_TOTAL FROM VW_SDL_PT A WHERE 1 = 1 AND A.MEET_CD = '003' AND A.STND_YEAR = ? -- 0:B_STND_YEAR 전년도 GROUP BY A.TMS
)AA, ( -- 현재년도 SELECT A.TMS , ROUND(SUM(A.POOL_TOTAL)/100000000,0) AS POOL_TOTAL FROM VW_SDL_PT A WHERE 1 = 1 AND A.MEET_CD = '003' AND A.STND_YEAR = ? -- 1:STND_YEAR 현재 년도 AND A.TMS <= ? -- 2:TMS 회차 GROUP BY A.TMS
)BB, (SELECT LEVEL AS LEV FROM DUAL CONNECT BY LEVEL <= 55) DU WHERE 1=1 AND DU.LEV = AA.TMS(+) AND DU.LEV = BB.TMS(+) ORDER BY LEV
