function fcInit(obj) {
	dsRollMakeStat.ClearData();
	dsRollUsgeStat.ClearData();
	dsRollMoveStat.ClearData();

}

/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 필수조건 체크 및 그리드 초기화
+-------------------------------------------------------------------------------------------------*/
function fcSearchCheck(obj) {

	fcInit(obj);

	return true;
}

/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearch(obj) {
	if ( !fcSearchCheck(obj) ) return;

	fcUsgeSearch(obj);
	fcMakeSearch(obj);
	fcMoveSearch(obj);


}

function fcUsgeSearch(obj) {


	var sServiceName = "rbo3030-service:searchRollUsgeStat";
	var sInDataSet   = "";
	var sOutDataSet  = "dsRollUsgeStat=dsRollUsgeStat ";

	fcd_AddParam("BRNC_CD",cboSearchBrncCd.Value);
	fcd_AddParam("STND_YEAR",cboStndYear.Value);

	if( cboRollGbn.Value == "005"){
		fcd_AddParam("ROLL_GBN"      , "^(00[13])$"); // 정규식표현		
    }else{
		fcd_AddParam("ROLL_GBN"      ,cboRollGbn.Value);		
    }

	fcd_AddParam("SEARCH_GBN", "001");

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);
}

function fcMakeSearch(obj) {


	var sServiceName = "rbo3030-service:searchRollMakeStat";
	var sInDataSet   = "";
	var sOutDataSet  = "dsRollMakeStat=dsRollMakeStat ";

	fcd_AddParam("BRNC_CD",cboSearchBrncCd.Value);
	fcd_AddParam("STND_YEAR",cboStndYear.Value);
	if( cboRollGbn.Value == "005"){
		fcd_AddParam("ROLL_GBN"      , "^(00[13])$"); // 정규식표현		
    }else{
		fcd_AddParam("ROLL_GBN"      ,cboRollGbn.Value);		
    }
	fcd_AddParam("SEARCH_GBN", "002");

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}


