function fcInit(obj) {
	dsRollPrhsCacu.ClearData();

}

/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 필수조건 체크 및 그리드 초기화
+-------------------------------------------------------------------------------------------------*/
function fcSearchCheck(obj) {

	fcInit(obj);

	return true;
}

/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearch(obj) {
	if ( !fcSearchCheck(obj) ) return;

	var sServiceName = "rbo3040-service:searchRollPrhsCacu";
	var sInDataSet   = "";
	var sOutDataSet  = "dsRollPrhsCacu=dsRollPrhsCacu ";
		//sOutDataSet += "dsIpMana=dsIpMana ";

	fcd_AddParam("STND_YEAR",cboStndYear.Value);
	fcd_AddParam("ROLL_GBN", cboRollGbn.Value);
	fcd_AddParam("BRNC_CD", cboBrncCd.Value);



	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);
}



function fcRollCfmSearch(){

	var sServiceName = "rbo3040-service:searchRollPrhsCfm";
	var sInDataSet   = "";
	var sOutDataSet  = "dsRollPrhsCfm=dsRollPrhsCfm ";


	fcd_AddParam("STND_YEAR",cboStndYear.Value);
	fcd_AddParam("ROLL_GBN", cboRollGbn.Value);
	fcd_AddParam("BRNC_CD", cboBrncCd.Value);



	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}

