	var sInDataSet   = "";
	var sOutDataSet  = "dsStrYear=dsStrYear ";

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}

/*-------------------------------------------------------------------------------------------------+
>>  저장 하기전에 필수항목 체크
+-------------------------------------------------------------------------------------------------*/
function fcSaveCheck(obj) {

    switch ( obj.id ) {
		case "btn_end" :
			if(cboStndYear.Value == null || cboStndYear.Value == ""){
				fnc_Message(SNIS_COM_1035,"N","기준년도");
				return;
			}
			break;
		case "btn_endcancel" :
			if(cboStndYear.Value == null || cboStndYear.Value == ""){
				fnc_Message(SNIS_COM_1035,"N","기준년도");
				return;
			}
		default:
			if(cboStndYear.Value == null || cboStndYear.Value == ""){
				fnc_Message(SNIS_COM_1035,"N","기준년도");
				return;
			}

			if(cboRollGbn.Value == null || cboRollGbn.Value == ""){
				fnc_Message(SNIS_COM_1035,"N","용지구분");
				return;
			}

			break;

	}



	return true;
}

/*-------------------------------------------------------------------------------------------------+
>>  저장 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSave(obj) {    
	if ( !fcSaveCheck() ) return;




	var sServiceName = "";

	sServiceName = "rbo3040-service:saveRollPrhsCacu";
	sInDataSet   = "";

	sOutDataSet  = "";


	fcd_AddParam("STND_YEAR", cboStndYear.Value);
	fcd_AddParam("ROLL_GBN", cboRollGbn.Value);
	fcd_AddParam("UNIT_PRICE", mskUnitPrice.Value);
	fcd_AddParam("ROLL_GBN", cboRollGbn.Value);



    switch ( obj.id ) {
		case "btn_cacu" : //01: 생성
			if(mskUnitPrice.Value == null || mskUnitPrice.Value == ""){
				fnc_Message(SNIS_COM_1035,"N","단가");
				return;
			}

			fcd_AddParam("JOB_GBN", "01");
			break;
		case "btn_cfm" : //02: 확정
			if(!fnc_Message(SNIS_RBM_B011,"Y")) return;

			fcd_AddParam("CFM_YN", "Y");
			fcd_AddParam("JOB_GBN", "02");
			break;
		case "btn_cfmcancel" : //03: 확정취소
			if(!fnc_Message(SNIS_RBM_B012,"Y")) return;
			fcd_AddParam("CFM_YN", "N");
			fcd_AddParam("JOB_GBN", "03");
			break;
		case "btnSave" :	//04: 수정
			fcd_AddParam("JOB_GBN", "04");

			sInDataSet   = "dsRollPrhsCacu=dsRollPrhsCacu:u ";

			sOutDataSet  = "dsRollPrhsCacu=dsRollPrhsCacu ";

			break;
		case "btn_end" :  //마감 
			if(!fnc_Message(SNIS_RBM_B013,"Y")) return;

			sServiceName = "rbo3030-service:saveRollUsgeStat";
			sInDataSet   = "";
			sOutDataSet  = "";

			fcd_AddParam("STND_YEAR", cboStndYear.Value);
			fcd_AddParam("ROLL_GBN", cboRollGbn.Value);

			fcd_AddParam("END_GBN", "Y");
			break;
		case "btn_endcancel" :  //마감취소 
			if(!fnc_Message(SNIS_RBM_B014,"Y")) return;

			sServiceName = "rbo3030-service:saveRollUsgeStat";
			sInDataSet   = "";
			sOutDataSet  = "";

			fcd_AddParam("STND_YEAR", cboStndYear.Value);
			fcd_AddParam("ROLL_GBN", cboRollGbn.Value);

			fcd_AddParam("END_GBN", "N");
			break;

	}


	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}

