/***************************************************************************************************
*                                      사용자 정의  처리 부분                                      *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {
	if ( !fnc_result(ErrorCode, ErrorMSG) ) return;

	// 구매수량산출  조회 완료 후
	if ( sServiceName == "rbo3040-service:searchRollPrhsCacu" ) {
		fcRollCfmSearch();
		fcRollEndSearch();

	}


	// 구매수량산출 저장 완료 후
	if ( sServiceName == "rbo3040-service:saveRollPrhsCacu" ) {
		fcRollCfmSearch();
		fcRollEndSearch();
		fcSearch();

	}


	// 용지재고 마감여부조회
	if ( sServiceName == "rbo3040-service:searchRollPrhsCfm" ) {


		if(dsRollPrhsCfm.GetRowCount() > 0){ //마감 
			if(dsRollPrhsCfm.GetColumn(0,"CFM_YN") == "Y"){
				btn_cfm.Enable = false;
				btn_cfmcancel.Enable = true;
				btn_cacu.Enable = false;


			}else if(dsRollPrhsCfm.GetColumn(0,"CFM_YN") == "N"){
				btn_cfm.Enable = true;
				btn_cfmcancel.Enable = false;
				btn_cacu.Enable = true;

			}else{
				btn_cfm.Enable = true;
				btn_cfmcancel.Enable = false;
				btn_cacu.Enable = true;

			}
		}

	}



	// 용지재고 저장 완료 후
	if ( sServiceName == "rbo3040-service:searchStndYear" ) {
		if(dsStrYear.GetRowCount() >0){
			setStndYear(dsStrYear.GetColumn(0,"STND_YEAR"));
		}


	}



	// 용지재고 저장 완료 후
	if ( sServiceName == "rbo3030-service:saveRollUsgeStat" ) {
		fcRollEndSearch();
		fcSearch();
	}


	// 용지재고 마감여부조회
	if ( sServiceName == "rbo3030-service:searchRollEnd" ) {


		if(dsRollEnd.GetRowCount() > 0){ //마감 
			btn_end.Enable = false;
			btn_endcancel.Enable = true;

		}else{		//마감 X
			btn_end.Enable = true;
			btn_endcancel.Enable = false;
		}


	}
}




