/*-------------------------------------------------------------------------------------------------+
>> dataset  load 이벤트시 조회되도록 한다.
+-------------------------------------------------------------------------------------------------*/
function fcDsOnLoadCompleted(obj,nErrorCode,strErrorMsg,nReason)
{

	switch ( obj.id ) {
		case "dsBrncCd" :
			dsBrncCd.InsertRow(0);
			dsBrncCd.SetColumn(0, "CD_NM", "전체");
			cboBrncCd.index = 0;
			break;

	}


}



/*-------------------------------------------------------------------------------------------------+
>> combo  onchange 이벤트시 조회되도록 한다.
+-------------------------------------------------------------------------------------------------*/
function fcCboOnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
    switch ( obj.id ) {
		case "cboStndYear" :
			fcSearch();
			fcRollCfmSearch();
			fcRollEndSearch();
			break;
		case "cboRollGbn" :
			fcSearch();
			fcRollCfmSearch();
			fcRollEndSearch();
			break;
		case "cboBrncCd" :
			fcSearch();
			fcRollCfmSearch();
			fcRollEndSearch();
			break;

	}
}

/*-------------------------------------------------------------------------------------------------+
>> 기준년도 콤보 dataset 설정 
+-------------------------------------------------------------------------------------------------*/
function setStndYear(sStrYear){

	var sCurYear  = ParseInt(fcd_GetSysdate("YYYY"));


	for(var i= sStrYear; i < sCurYear +1; i++){
		nRow = dsStndYear.AddRow();

		dsStndYear.SetColumn(nRow,"CD",i);
		dsStndYear.SetColumn(nRow,"CD_NM",i);		
	}

	cboStndYear.value = sCurYear;


    fcSearch();
}


function fcRollEndSearch(){

	var sServiceName = "rbo3030-service:searchRollEnd";
	var sInDataSet   = "";
	var sOutDataSet  = "dsRollEnd=dsRollEnd ";

	fcd_AddParam("STND_YEAR",cboStndYear.Value);
	fcd_AddParam("ROLL_GBN", cboRollGbn.Value);



	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}
