function fcInit(obj) {
    dsList.ClearData();
    dsUploadFile.ClearData();
}


/*-------------------------------------------------------------------------------------------------+
>>  Á¶È¸ ¹öÆ° Å¬¸¯ ãÁ Ã³¸®
+-------------------------------------------------------------------------------------------------*/

function fcSearchCheck(obj) {
	fcInit(obj);
	return true;
}


//Ã·ºÎÆÄÀÏ Á¶È¸ Àü Check
function fcSearchFileCheck() {
	dsUploadFile.ClearData();
	return true;
}


function fcSearch(obj) 
{
    if (!fcSearchCheck(obj)) return;	// ÇÊ¼öÁ¶°Ç Ã¼Å© ¹× ±×¸®µå ÃÊ±âÈ­

	var sServiceName = "rbo4010-service:searchList";
    var sInDataSet   = "";
    var sOutDataSet  = "dsList=dsList ";     

	fcd_AddParam("PARTS_LCD", cboSchPartsLcd.Value);	// ¹ß¸Å±â
	fcd_AddParam("PARTS_MCD", cboSchPartsMcd.Value);	// ºÎÇ°¸ðµ¨ºÐ·ù 

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);

}


//Ã·ºÎÆÄÀÏ Á¶È¸
function fcSearchFile(sAttFileKey)
{	
	if ( !fcSearchFileCheck() ) return;

    var sServiceName = "common_file-service:searchFileMana";
    var sInDataSet   = "";
    var sOutDataSet  = "dsUploadFile=dsUploadFile ";

    //Á¶È¸Á¶°Ç
    fcd_AddParam("ATT_FILE_KEY", sAttFileKey);

    fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}


