function fcSaveUserCheck(obj) {
	// ·Î±×ÀÎÇÑ À¯Àú°¡ º»Àå(±¤¸í) °ü¸®ÀÚ°¡ ¾Æ´Ï¸é,
 	if( gdsUser.GetColumn(0, "BRNC_CD") != "00" ) {
		// ÁöÁ¡±ÇÇÑÀÌ ¾ø½À´Ï´Ù . \r\n [ @ ] ÀÛ¾÷ ÇÒ ¼ö ¾ø½À´Ï´Ù.
		fnc_Message(SNIS_RBM_0007, "N", "ÀúÀå");
		return;
 	}
}


function fcSaveCheck(obj) {  

 	// º¯°æµÈ ÀÚ·á°¡ ÀÖ´ÂÁö Ã¼Å©
	if ( !fnc_DatasetChangeCheck(dsList) && 
         !fnc_DatasetChangeCheck(dsUploadFile)) {
		// ÀúÀå ÇÒ ÀÚ·á°¡ ¾øÀ½
		fnc_Message(SNIS_COM_1006, "N");
		return false;
	}

	// ÇÊ¼öÇ×¸ñÃ¼Å©
	oDataSet          = dsList;
	sMandatoryColID   = "PARTS_LCD|PARTS_MCD|PARTS_NM|MADE_GBN|USE_YN";
	sMandatoryColName = "¹ß¸Å±â|ºÎÇ°¸ðµ¨ºÐ·ù|ºÎÇ°¸í|±¸ºÐ|»ç¿ë¿©ºÎ";
	oGrid             = grdList;
	if ( fnc_GetMandatoryViolateRow(oDataSet, sMandatoryColID, sMandatoryColName, oGrid) > -1 ) {
		return false;
	}
	return true;
}


function fcSave(obj) {

	if ( !fcSaveCheck() ) return;

	var sServiceName = "rbo4010-service:SavePartsModel";
	var sInDataSet   = "dsList=dsList:u dsUploadFile=dsUploadFile:u";
	var sOutDataSet  = "dsList=dsList ";

	var confirmMsg = "ÀúÀåÇÏ½Ã°Ú½À´Ï±î?";
    if(fnc_Message(SNIS_CRA_B999, "Y", confirmMsg)) {           

		//Ã·ºÎÆÄÀÏ ¼­¹ö ¾÷·Îµå 
		if( dsUploadFile.GetRowCount() > 0 ) {
			for (var i = 0 ; i < dsUploadFile.GetRowCount() ; i++ ) {
				if( dsUploadFile.GetRowType(i) == "insert" ) {

					var rtn_arr = fnc_FileWrite(  divBtnLst.HttpFile 
												, dsUploadFile.GetColumn(i, "FILE_PATH" )
												, dsUploadFile.GetColumn(i, "FILE_NM" ) 
												,"");

					var fileSize = divBtnLst.HttpFile.GetFileSizeLocal(dsUploadFile.GetColumn(i, "FILE_PATH" ));

					if ( rtn_arr[0] != "SUCC" ) {

						fnc_Message(SNIS_COM_1050, "N", rtn_arr[1]);
						return false;
					}

					dsUploadFile.SetColumn(i, "FILE_PATH", rtn_arr[1] );
					dsUploadFile.SetColumn(i, "FILE_SIZE", fileSize );
					dsUploadFile.SetColumn(i, "REAL_FILE_NM", rtn_arr[3] );

					//Trace(dsUploadFile.savexml());

				} // end if
			}
		} // end 

		var nRow = dsList.row;
		strCurRow = nRow;

		fcd_AddParam("PARTS_CD", dsList.GetColumn(nRow, "PARTS_CD"));

		fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
	}
}

