function fcImgOnClick(obj,nX,nY)
{
	switch ( obj.id ) 
	{	
		case "imgModel" : 
			if (imgModel.ImageID == "") return;

			var sLocalFileName  = fcd_GetSelectFile("Save", dsUploadFile.GetColumn(0, "FILE_NM")); // ÆÄÀÏ¸í			
			var sServerFileName = dsUploadFile.GetColumn(0, "FILE_PATH");	// ¼­¹öÀÇ ÆÄÀÏÀ§Ä¡ ¹× ÆÄÀÏ¸í
			var sFileSize       = 0;
			if ( sLocalFileName == null ) return;

			fnc_FileRead(divBtnLst.HttpFile, sLocalFileName[0] + "\\" + sLocalFileName[1], sServerFileName);
			break;
	}
}
