// 부품명 조회
function getComboschPartsNm() {

	var sServiceName = "rbo4040-service:searchSchParts";
    var sInDataSet   = "";
    var sOutDataSet  = "dsSchP=dsSchP";     

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}


function fcSearchCheck(obj) {

	fcInit(obj);

	return true;
}


// 모델별 부품 입고 정보 조회
function fcSearch(obj) {

    var startDt = substr(calStartDate.Value, 0, 8);
    var endDt = substr(calEndDate.Value, 0, 8);

    if ( startDt > endDt ) {		// 날짜 유효성 체크

		// 저장 할 자료가 없음
		fnc_Message(SNIS_RBM_E002, "N");
		return;
    }

    if ( !fcSearchCheck(obj) ) return;

	var sServiceName = "rbo4020-service:searchList";
    var sInDataSet   = "";
    var sOutDataSet  = "dsList=dsList ";     

	fcd_AddParam("PARTS_LCD"	, cboSchPartsLcd.Value	);		// 발매기 코드
	fcd_AddParam("PARTS_MCD"	, cboSchPartsMcd.Value	);		// 부품그룹코드
	fcd_AddParam("PARTS_CD"		, cboSchPartsCd.Value				);		// 부품코드
	fcd_AddParam("IN_DT_FROM"	, substr(calStartDate.Value, 0, 8)	);		// 입고 시작 기간
	fcd_AddParam("IN_DT_TO"		, substr(calEndDate.Value, 0, 8)	);		// 입고 종료 기간

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);
}


/*-------------------------------------------------------------------------------------------------+
>>  저장 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/

function fcSaveCheck(obj) {  

 	// 변경된 자료가 있는지 체크
	if ( !fnc_DatasetChangeCheck(dsList) ) {
		// 저장 할 자료가 없음
		fnc_Message(SNIS_COM_1006, "N");
		return false;
	}

	// 필수항목체크
	oDataSet          = dsList;
	sMandatoryColID   = "PARTS_LCD|PARTS_MCD|PARTS_CD|IN_CNT|IN_DT";
	sMandatoryColName = "발매기|부품모델분류|부품코드|입고수량|입고일자";
	oGrid             = grdList;
	if ( fnc_GetMandatoryViolateRow(oDataSet, sMandatoryColID, sMandatoryColName, oGrid) > -1 ) {
		return false;
	}

	return true;
}


function fcSave(obj) {

	if ( !fcSaveCheck() ) return;

	var sServiceName = "rbo4020-service:SavePartsIn";
	var sInDataSet   = "dsList=dsList:u ";
	var sOutDataSet  = "dsList=dsList ";

	var confirmMsg = "저장하시겠습니까?";
    if(fnc_Message(SNIS_CRA_B999, "Y", confirmMsg)) {           

		fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
	}
}

