function fcAddRow(obj) {

	var nRow;	// Record Add

	nRow = dsList.AddRow();

	dsList.SetColumn(nRow, "BRNC_CD", "00"					);	// 본장 : 광명
	dsList.SetColumn(nRow, "IN_DT"  , substr(getDate(),0,8)	);	// 입고일자

	grdList.SetFocus(nRow);		// 포커스준 Row의 첫번째 Cell에 포지션을 둔다.
	grdList.SetCellPos(0);
}


