// 부품명 검색 버튼 클릭시 ( 입고이력등록팝업 호출 )
function fcPartsDialoge(obj,nRow,nCell,strVal,nPivotIndex)
{
	var sFormId 	= "";
	var sArgs 		= "";
	var arrReturn   = array(4);

	sFormId = "frm_rbo::RBO4020P.xml";

	// 해당 지점 > 해당 발매기 > 해당 부품분류 > 해당 부품명 > 해당 구분
	sArgs = "pv_brncCd = "    	+ quote(dsList.GetColumn(nRow, "BRNC_CD"		)) +	// 지점
			" pv_partsLcd = " 	+ quote(dsList.GetColumn(nRow, "PARTS_LCD"		)) +	// 발매기
			" pv_partsMcd = " 	+ quote(dsList.GetColumn(nRow, "PARTS_MCD"		)) +	// 부품분류
			" pv_partsCd = "  	+ quote(dsList.GetColumn(nRow, "PARTS_CD"		)) +	// 부품코드
			" pv_partsNm = "  	+ quote(dsList.GetColumn(nRow, "PARTS_NM"		)) +	// 부품명
			" pv_madeGbn = "  	+ quote(dsList.GetColumn(nRow, "MADE_GBN"		)) +	// 구분(원산지)
			" pv_inDt = "	  	+ quote(dsList.GetColumn(nRow, "IN_DT"			));

	arrReturn = Dialog(sFormId, sArgs, 568,512, "");

	fcSearch();
}


function dsList_OnRowPosChanged(obj,nOldRow,nRow)
{
	switch( obj.id ) {
		case "dsList" :
			// 신규일 시에만 수정 가능
			if(obj.getRowType(nRow) == "insert") {
				calInDt.Enable     = true;
				cboPartsLcd.Enable = true;
				cboPartsMcd.Enable = true;
				cboPartsCd .Enable = true;
				edInCnt.Enable = true;
			} else {
				calInDt.Enable     = false;
				cboPartsLcd.Enable = false;
				cboPartsMcd.Enable = false;
				cboPartsCd .Enable = false;
				edInCnt.Enable = false;
			}

			// Filter 조절
			if( fnc_IsNull(obj.GetColumn(nRow, "PARTS_LCD")) ) {
				dsMcd.Filter("CD==''");
				dsCd .Filter("PARTS_CD==''");
			} else {
				if( fnc_IsNull(obj.GetColumn(nRow, "PARTS_MCD")) ) {
					dsCd .Filter("PARTS_CD==''");
				}
			}
		break;
	}

	//입고일자가 금일인 경우에만 수정 가능.
	// var strInDt = dsList.GetColumn(nRow, "IN_DT");

	// if ( strInDt < substr(getDate(), 0, 8)) {
		// edInCnt.Enable = false;
	// } else {
		// edInCnt.Enable = true;
	// }
}


function fcDsOnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	switch( obj.id ) {
		case "dsList" :
			if( strColumnID == "PARTS_LCD" ) {		
				if( varNewValue == "001" ) dsMcd.Filter( "CD_TERM1==" + quote(varNewValue) );
				if( varNewValue == "002" ) dsMcd.Filter( "CD_TERM2==" + quote(varNewValue) );
				if( varNewValue == "003" ) dsMcd.Filter( "CD_TERM3==" + quote(varNewValue) );

				cboPartsMcd.Enable = true;
				cboPartsMcd.Index = 0;
			}

			if( strColumnID == "PARTS_MCD" ) {	
				var sPartLcd = obj.GetColumn(nRow, "PARTS_LCD");
				var sPartMcd = varNewValue;

				obj.SetColumn(nRow, "PARTS_CD", "");

				dsCd.Filter("PARTS_LCD==" + quote(sPartLcd) + " AND " +
						    "PARTS_MCD==" + quote(sPartMcd) );
			}

			if( strColumnID == "PARTS_CD" ) {	
				//원산지 Setting
				var strMadeGbn = dsCd.LookUp("PARTS_CD", varNewValue, "MADE_GBN");
				dsList.SetColumn(nRow, "MADE_GBN", strMadeGbn);	// 원산지 구분

			}
		break;
	}
}


function fcCombo_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	switch( obj.Id ) {
		case "cboSchPartsLcd" :
			if( length(strCode) > 0){
				var strFilter="";
				if( strCode == "001") strFilter = "CD_TERM1=='001'";
				if( strCode == "002") strFilter = "CD_TERM2=='002'";
				if( strCode == "003") strFilter = "CD_TERM3=='003'";		
				dsPartsMcd.Filter(strFilter);
				cboSchPartsMcd.Enable 	= true;
				cboSchPartsMcd.Index 	= 0;
			}else{
				strFilter = "CD_TERM1=='001'";
				dsPartsMcd.Filter(strFilter);
				cboSchPartsMcd.Enable 	= true;
				cboSchPartsMcd.Index 	= 0;
			}
			fcSearch();
			break;

		case "cboSchPartsMcd" :
			if( length(strCode) > 0){
				var strFilter = "";
				if( fnc_IsNull(cboSchPartsLcd.Value)) {
					//발매기 전체일시
					strFilter="PARTS_MCD=='"+strCode+"'||PARTS_CD==''";	
				} else {
					//발매기 선택시
					strFilter="PARTS_LCD=='"+cboSchPartsLcd.Value+"'&&PARTS_MCD=='"+strCode+"'||PARTS_CD==''";	
				}

				dsSchP.Filter(strFilter);
				cboSchPartsCd.Index 	= 0;
			} else {
				dsSchP.Filter("");
				cboSchPartsCd.Index = 0;
			}

			fcSearch();
			break;
		case "cboSchPartsCd" :
			fcSearch();
			break;
	}
}


// 발매기 '전체' 삽입
function fcDsOnLoadCompleted(obj,nErrorCode,strErrorMsg,nReason)
{
	switch ( obj.Id ) {
		case "dsPartsLcd" :

			if( nReason == 0){
				//데이터셋 복사( 조회조건용 / 입력용 )
				dsLcd.Copy(obj);

				obj.fireEvent = false;
				obj.insertRow(0);
				obj.setColumn(0,"CD","");
				obj.setColumn(0,"CD_NM","전체");
				obj.fireEvent = true;
				cboSchPartsLcd.Index = 0;
			}
		break;

		case "dsPartsMcd" :

			if( nReason == 0){
				//데이터셋 복사( 조회조건용 / 입력용 )
				dsMcd.Copy(obj);

				obj.fireEvent = false;
				obj.insertRow(0);
				obj.setColumn(0,"CD","");
				obj.setColumn(0,"CD_NM","전체");
				obj.setColumn(0,"CD_TERM1","001");
				obj.setColumn(0,"CD_TERM2","002");
				obj.setColumn(0,"CD_TERM3","003");
				obj.fireEvent = true;
				cboSchPartsMcd.Index = 0;
			}
		break;
	}
}
