/*-------------------------------------------------------------------------------------------------+
>>  최초 화면 Load時 처리 할 사항
+-------------------------------------------------------------------------------------------------*/
function fcFormLoadSetting(obj) {  
    sBUTTONLIST   = 'FTTFTFT';

 	//화면환경 설정
	sFORMCAPTION  = obj.Title;
	sFORMLOCATION = "";

	//calStartDate.Value = substr(getDate(), 0, 6) + "01";	// 현재달의 첫날(매월 1일)
	//calEndDate.Value = getDate();


	// 지점입고(완료) 
	calStartInDate.Value = substr(getDate(), 0, 6) + "01";	// 현재달의 첫날(매월 1일)
	calEndInDate.Value = getDate();


	//화면 정보 Display 및 Button 권한 설정
	fnc_FormLoadSetting(obj, sFORMCAPTION, sFORMLOCATION, sBUTTONLIST, sUSERAUTH);

	//초기 화면 BK Color설정 
	fnc_SetBKColor(this);

	//공통코드 조회
	fcd_GetComboDs(dsInComCd);

	fcd_GetAlarmDs("dsAlarm", "007");



	if(dsAlarm.GetRowCount() >0 && Length(dsAlarm.LookUp("USER_ID",sUserId,"USER_ID")) >0){
		btnAdminIn.Visible = true;
		btnAdminCancleIn.Visible = true;

	}

    setTimer(1, 100);

	//조회조건 전체 추가
	dsPartsLcd.InsertRow(0);		
	dsPartsLcd.SetColumn(0 ,"CD"   , "" );
	dsPartsLcd.SetColumn(0 ,"CD_NM", "전체");
	cboSchPartsLcd.Index = 0;	


	//조회조건 전체 추가
	dsBrncCd.InsertRow(0);		
	dsBrncCd.SetColumn(0 ,"CD"   , "" );
	dsBrncCd.SetColumn(0 ,"CD_NM", "전체");
	cboSchBrncCd.Index = 0;	



    // 목록 조회 
    fcSearch();  

}


/*-------------------------------------------------------------------------------------------------+
>>  최초 화면 UNLoad時 처리 할 사항
+-------------------------------------------------------------------------------------------------*/
function fcFormUnloadProcess(obj) {
	//자료 변경 여부를 체크하고자 하는  DataSet Object를 넘긴다.
	var sDataset = "dsList";
	if (!fnc_FormUnloadCheck(sDataset  , sFormId)) return false;
}


/*-------------------------------------------------------------------------------------------------+
>>  단축키 처리(Form KeyDown)
+-------------------------------------------------------------------------------------------------*/
function fcHotKey(obj,objSenderObj,nChar,bShift,bControl,bAlt,nLLParam,nHLParam) {

	// Return Key Press時 Next Component focus
	if ( nChar == 13 ) {
		var oFocusComponent = GetNextComponent(true);
		oFocusComponent.setfocus();
		return;
	}
}


