	fcBrncInInit(obj);

	return true;
}


function fcSearch(obj) {

    var startDt = substr(calStartDate.Value, 0, 8);
    var endDt = substr(calEndDate.Value, 0, 8);

    if ( startDt > endDt ) {		// 날짜 유효성 체크

		fnc_Message(SNIS_RBM_E002, "N");
		return;
    }


    if ( !fcSearchCheck(obj) ) return;

	var sServiceName = "rbo4060-service:searchAprvList";
    var sInDataSet   = "";
    var sOutDataSet  = "dsList=dsList ";     

	fcd_AddParam("REQ_DT_FROM"	, startDt				);		// 신청 시작 기간
	fcd_AddParam("REQ_DT_TO"	, endDt					);		// 신청 종료 기간
	fcd_AddParam("PARTS_LCD"	, cboSchPartsLcd.Value	);		// 발매기 코드

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);
}


// 지점입고 (완료) 조회
function fcBrncInSearch(obj) {

    var startInDt = substr(calStartInDate.Value, 0, 8);
    var endInDt = substr(calEndInDate.Value, 0, 8);

    if ( startInDt > endInDt ) {		// 날짜 유효성 체크

		fnc_Message(SNIS_RBM_E002, "N");
		return;
    }


    if ( !fcSearchBrncInCheck(obj) ) return;

	var sServiceName = "rbo4060-service:searchBrncList";
    var sInDataSet   = "";
    var sOutDataSet  = "dsBrncList=dsBrncList ";     

	fcd_AddParam("BRNC_IN_FROM"	, startInDt				);		// 지점입고 시작 기간
	fcd_AddParam("BRNC_IN_TO"	, endInDt				);		// 지점입고 종료 기간
	fcd_AddParam("BRNC_CD"		, cboSchBrncCd.Value	);		// 지점 코드

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);
}


/*-------------------------------------------------------------------------------------------------+
>>  저장 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSaveCheck(obj) {  

	//변경된 자료가 있는지 체크
	if ( !fnc_DatasetChangeCheck(dsList)) {
		//저장 할 자료가 없음
		fnc_Message(SNIS_COM_1006, "N");	
		return false;
	}

	//필수항목체크
	oDataSet          = dsList;
	sMandatoryColID   = "";
	sMandatoryColName = "";
	oGrid             = grdList;
	if ( fnc_GetMandatoryViolateRow(oDataSet, sMandatoryColID, sMandatoryColName, oGrid) > -1 ) {
	    return false;
	}	
	return true;
}

function fcCancelSaveCheck(obj) {  

	//변경된 자료가 있는지 체크
	if ( !fnc_DatasetChangeCheck(dsBrncList)) {
		//저장 할 자료가 없음
		fnc_Message(SNIS_COM_1006, "N");	
		return false;
	}

	//필수항목체크
	oDataSet          = dsBrncList;
	sMandatoryColID   = "";
	sMandatoryColName = "";
	oGrid             = grdBrncList;
	if ( fnc_GetMandatoryViolateRow(oDataSet, sMandatoryColID, sMandatoryColName, oGrid) > -1 ) {
	    return false;
	}	
	return true;
}


// 입고 저장
function fcInSave(sAdminYn)
{
	if(sAdminYn == null) sAdminYn ="";
	if ( !fcSaveCheck() ) return;


	for(var i=0; i < dsList.rowcount; i++) {
		if( dsList.GetColumn(i, "CHK") == "1" ) {
			dsList.SetColumn(i, "BRNC_IN_DT", substr(getDate(), 0, 8)			);	// 지점입고일자
			// 지점입고자는 승인이 떨어진 후에 데이터셋에 담으므로 dsList_tmp 저장 직전에 세팅.
			dsList.SetColumn(i, "BRNC_IN_CNT", dsList.GetColumn(i, "APRV_CNT"));	// 지점입고수량 (승인수량)

			dsList.SetColumn(i, "BRNC_CNL_YN", "N"								);	// 지점입고 취소 여부(입고)	
		} 
	}

	dsList.fireEvent = false;
    dsList.Filter("CHK=='1'");
    dsList_tmp.CopyF(dsList);
    dsList.UnFilter();  
    dsList.fireEvent = true;

	var sTmpUserId = "";
	if(sAdminYn == "Y"){
		//팝업 조회 

		var sFormId 	= "";
		var sArgs 		= "";
		var arrReturn   = array(2);

		sFormId = "frm_rbo::RBO4060P.xml";
		sArgs = "";	

		arrReturn = Dialog(sFormId, sArgs, 416, 496, "");

		if(arrReturn.length>0){
			sTmpUserId = arrReturn[0];	
		}else{		
			return;		
		}


	}else{
		sTmpUserId = dsOutUser.GetColumn(0, "USER_ID");
	}

    for(var i=0;i<dsList_tmp.GetRowCount();i++){
		dsList_tmp.SetColumn(i,"BRNC_IN_ID", sTmpUserId	);

    }



    var sServiceName = "";
	var sInDataSet   = "";
	var sOutDataSet  = "";       

	sServiceName = "rbo4060-service:SaveBranchIn";
	sInDataSet   = "dsList=dsList_tmp ";
	sOutDataSet  = "";		

	fcd_AddParam("SEND_VALUE"	, strFlag);		// 본점(지점)입고

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);
}


// 입고취소 저장
function fcCancelSave(sAdminYn)
{
	if(sAdminYn == null) sAdminYn ="";

	if ( !fcCancelSaveCheck() ) return;

	for(var i=0; i < dsBrncList.rowcount; i++) {
		if( dsBrncList.GetColumn(i, "CHK") == "1" ) {
			dsBrncList.SetColumn(i, "BRNC_IN_DT", substr(getDate(), 0, 8)			);	// 지점입고일자
			// 지점입고자는 승인이 떨어진 후에 데이터셋에 담으므로 dsBrncList_tmp 저장 직전에 세팅.
			//dsBrncList.SetColumn(i, "BRNC_IN_CNT", dsBrncList.GetColumn(nRow, "APRV_CNT"));	// 지점입고취소 수량
			dsBrncList.SetColumn(i, "BRNC_CNL_YN", "Y"								);	// 지점입고 취소 여부(취소)
		} 
	}

    dsBrncList.fireEvent = false;
    dsBrncList.Filter("CHK=='1'");
    dsBrncList_tmp.CopyF(dsBrncList);
    dsBrncList.UnFilter();  
    dsBrncList.fireEvent = true;

   	var sTmpUserId = "";
	if(sAdminYn == "Y"){
		sTmpUserId = sUserId;
	}else{
		sTmpUserId = dsOutUser.GetColumn(0, "USER_ID");
	}

    for(var i=0;i<dsList_tmp.GetRowCount();i++){
		dsBrncList_tmp.SetColumn(i,"BRNC_IN_ID", sTmpUserId	);

    }



    var sServiceName = "";
	var sInDataSet   = "";
	var sOutDataSet  = "";       

	sServiceName = "rbo4060-service:SaveBranchIn";
	sInDataSet   = "dsBrncList=dsBrncList_tmp ";
	sOutDataSet  = "";		

	fcd_AddParam("SEND_VALUE"	, strFlag);		// 본점(지점)입고

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);
}

