/***************************************************************************************************
*                                      사용자 정의  처리 부분                                      *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {

	if ( !fnc_result(ErrorCode, ErrorMSG) ) return;



	// 지점입고(신청) 조회 완료 후
	if ( sServiceName == "rbo4060-service:searchAprvList" ) {

		// 지점입고 (완료) 조회
		fcBrncInSearch();
	}

	// 지점입고(완료) 조회 완료 후
	if ( sServiceName == "rbo4060-service:searchBrncList" ) {
		Trace(dsBrncList.savexml());
	}	



	// 사용자인증 정보 조회 후
	if ( sServiceName == "rbs3040-service:searchSignPswd" ) {  



        if ( dsOutUser.GetRowCount() == 0 ) {       
            fnc_Message(SNIS_COM_0003, "N");
            DivBrncNo.edtUserId.Text = "";
            DivBrncNo.edtPswd.Text   = "";
            DivBrncNo.edtUserId.SetFocus();
            return;
        }

        if ( dsOutUser.GetColumn(dsOutUser.currow, "ISVALID") != "T" ) {
            fnc_Message(SNIS_COM_0004, "N");
            DivBrncNo.edtPswd.Text   = "";
            DivBrncNo.edtPswd.SetFocus();
            return;
        }



		var nCnt;

     	if 	( strFlag == "IN" )	{     	
     	    dsList.fireEvent = false;
			dsList.Filter("CHK=='1' && BRNC_CD!="+quote(dsOutUser.GetColumn(0,"BRNC_CD")));
			 nCnt = dsList.GetRowCount();
			dsList.UnFilter();
			dsList.fireEvent = true;

     	}else if ( strFlag == "CANCEL" ){
			dsBrncList.fireEvent = false;
			dsBrncList.Filter("CHK=='1' && BRNC_CD!="+quote(dsOutUser.GetColumn(0,"BRNC_CD")));
			 nCnt = dsBrncList.GetRowCount();
			dsBrncList.UnFilter();
			dsBrncList.fireEvent = true;     	
     	}





        if( nCnt > 0){
			alert("서명자의 지점외 타지점 데이타를 선택하셨습니다!\n확인바랍니다");
			return;
        }

        DivBrncNo.Visible = false;		// DIV       



		if 		( strFlag == "IN" )		fcInSave();			// 입고저장		
		else if ( strFlag == "CANCEL" )	fcCancelSave();		// 입고취소 저장		
	}


	// 저장 완료 후
	if ( sServiceName == "rbo4060-service:SaveBranchIn" ) {
		DivBrncNo.edtUserId.Text = "";
		DivBrncNo.edtPswd.Text = "";
		strFlag = "";
		fcSearch();		
	}	
}


