function fcCalOnChanged(obj,strOldText,strNewText)
{
	switch ( obj.Id ) {
		case "calStartInDate" :
		case "calEndInDate" :
			var startInDt = substr(calStartInDate.Value, 0, 8);
			var endInDt = substr(calEndInDate.Value, 0, 8);
			if ( startInDt > endInDt ) {		// 날짜 유효성 체크
				return;
			}
			fcBrncInSearch();	
		break;

	}
}
