}

/*-------------------------------------------------------------------------------------------------+
>>  최초 화면 UNLoad時 처리 할 사항
+-------------------------------------------------------------------------------------------------*/
function fcFormUnloadProcess(obj) {
	nCloseFlag = 0;

	for( var i = 0; i < Tab.TabCount ; i++) {
		if( nCloseFlag == 1 ) {
			break;
		}

		if( tab.GetItem(i).fcFormUnloadProcess() ) {			
			return false;
		} else {
			nCloseFlag = 1;
		}		
	}

	if ( global.FrameBottom != null ) {
		global.FrameBottom.fcDeleteTab(sFormID);
	}
}

