function fcInit(obj) {
	dsRptPrepSitu.ClearData();	
}


/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 필수조건 체크 및 그리드 초기화
+-------------------------------------------------------------------------------------------------*/
function fcSearchCheck(obj) {
	fcInit(obj);

	return true;
}

/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearch(obj) {
	var sServiceName = "rsm2010-service:searchMeetCd";  // Grid 조회
	var sInDataSet   = "";
	var sOutDataSet  = "dsMeetCd=dsMeetCd";     

	fcd_AddParam("RACE_DAY", calRaceDay.Value);    
	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}

function fcSearchInfo(obj) {
	if ( !fcSearchCheck(obj) ) return;

	var sServiceName = "rbr1060-service:searchRaceInfo";
	var sInDataSet   = "";
	var sOutDataSet  = "dsRaceInfo=dsRaceInfo dsRptPrepSitu=dsRptPrepSitu dsRptPrepSituNum=dsRptPrepSituNum dsBrncSignftPnt=dsBrncSignftPnt";

	fcd_AddParam("RACE_DAY", calRaceDay.Value);    
	fcd_AddParam("MEET_CD", rdoMeetCd.Value);
	fcd_AddParam("BRNC_CD",  "001");				//본장

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);
}

/*-------------------------------------------------------------------------------------------------+
>>  저장 하기전에 필수항목 체크
+-------------------------------------------------------------------------------------------------*/
function fcSaveCheck(obj) {
	// 변경된 자료가 있는지 체크
	if ( !fnc_DatasetChangeCheck(dsRptPrepSitu) ) {
		// 저장 할 자료가 없음
		fnc_Message(SNIS_COM_1006, "N");
		return false;
	}

	// 필수항목체크
	oDataSet          = dsRptPrepSitu;
	sMandatoryColID   = "RACE_DAY|COMM_NO";
	sMandatoryColName = "경주일자|지점";
	oGrid             = grdRptPrepSitu;

	if ( fnc_GetMandatoryViolateRow(oDataSet, sMandatoryColID, sMandatoryColName, oGrid) > -1 ) {
		return false;
	}

	return true;
}

/*-------------------------------------------------------------------------------------------------+
>>  저장 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSave(obj) {
	if ( !fcSaveCheck() ) return;

	var sServiceName = "";
	var sInDataSet   = "";
	var sOutDataSet  = "";

	sServiceName = "rbr1060-service:saveRptPrepSitu";
	sInDataSet   = "dsRptPrepSitu=dsRptPrepSitu:u dsRptPrepSituNum=dsRptPrepSituNum:u";
	sOutDataSet  = "dsRptPrepSitu=dsRptPrepSitu dsRptPrepSituNum=dsRptPrepSituNum dsBrncSignftPnt=dsBrncSignftPnt";

	fcd_AddParam("RACE_DAY", calRaceDay.Value);    
	fcd_AddParam("BRNC_CD",  "001");

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}

