/***************************************************************************************************
*                                      사용자 정의  처리 부분                                      *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {
	if ( !fnc_result(ErrorCode, ErrorMSG) ) return;

    if(sServiceName == "rsm2010-service:searchMeetCd") {
		rdoMeetCd.Value = dsMeetCd.GetColumn(0,"CD");			
		if(dsMeetCd.rowcount > 0) { // 경주일이 있을 경우만 검색
			fcSearchInfo();
		} else {
			fcInit();
		}
		return;
    } else if(sServiceName == "rbr1060-service:searchRaceInfo") {
		if (dsRptPrepSitu.rowcount() == 0) {
			var nRow = dsRptPrepSitu.AddRow();
			dsRptPrepSitu.SetColumn(nRow, "RACE_DAY", calRaceDay.Value);
			dsRptPrepSitu.SetColumn(nRow, "COMM_NO",  "00");
		}
		stRaceDesc.text = dsRaceInfo.GetColumn(0, "RACE_INFO");
		tab0.tab1.mskWrkgNum.SetFocus();
    }

}




/*-------------------------------------------------------------------------------------------------+
>> Enter Key 이벤트시 조회되도록 한다.
+-------------------------------------------------------------------------------------------------*/


function fcOnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	if(obj.Id == "cboBrncCd"){
		fcSearch();
	}

}


function fcDsOnLoadCompleted(obj,nErrorCode,strErrorMsg,nReason)
{
	if (obj.id == "dsBrncCd" && nReason == 0) {
		if (sApro != "T") dsBrncCd.Filter("CD='"+fcGetBrncCd()+"'");
		else              dsBrncCd.UnFilter(); 
		cboBrncCd.value = fcGetBrncCd();

	} else if(obj.id == "dsRptPrepSituNum") {
		if (dsRptPrepSitu.rowcount() == 0) {
			var nRow = dsRptPrepSitu.AddRow();
			dsRptPrepSitu.SetColumn(nRow, "RACE_DAY", calRaceDay.Value);
			dsRptPrepSitu.SetColumn(nRow, "COMM_NO",  cboBrncCd.Value);
		}
		tab0.tab1.mskWrkgNum.Value = dsRptPrepSituNum.Sum("WRKG_NUM");
		tab0.tab1.meDutyOffNum.Value = dsRptPrepSituNum.Sum("DUTY_OFF_NUM");
	}
}

function fcCboOnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	if (obj.id == "cboBrncCd") fcSearch(obj);
}

function fcdsOnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	if (obj.id == "dsRptPrepSituNum") {
		tab0.tab1.mskWrkgNum.Value = dsRptPrepSituNum.Sum("WRKG_NUM");
		tab0.tab1.meDutyOffNum.Value = dsRptPrepSituNum.Sum("DUTY_OFF_NUM");	
	}
}

function fcGetBrncCd()
{
	var sBrncCd = "";
	var nRow = dsBrncCd.FindRow("CD_NM2", gdsUser.GetColumn(0, "TEAM_CD"));
	if (nRow > -1) sBrncCd = dsBrncCd.GetColumn(nRow, "CD");
	else sBrncCd = gdsUser.GetColumn(0, "BRNC_CD");
	return sBrncCd;
}
