/*-------------------------------------------------------------------------------------------------+
>>  취소 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcInit(obj) {
	dsOrganExam.ClearData();
    dsOrganExam001.ClearData();
    dsOrganExam004.ClearData();
    dsOrganExam005.ClearData();
    dsOrganExamSave.ClearData();
    dsOrganExamDelete.ClearData();
}

/*-------------------------------------------------------------------------------------------------+
>>  화면 종료(닫기) 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcEnd(obj) {
	// 화면 종료
	Close();
}

/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 체크 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearchCheck(obj) {
    //조회조건 체크및 trim처리
    fcInit(obj);    //기존 데이터셋 초기화
    return true;
}

/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearch(obj) {
    if ( !fcSearchCheck(obj) ) return;

    var sServiceName = "rbr5010-service:searchOrganExam";
    var sInDataSet   = "";
    var sOutDataSet  = "dsOrganExam=dsOrganExam ";

    fcd_AddParam("MEET_CD", "003");			// 경륜구분코드	
    fcd_AddParam("STND_YEAR", cboStndYear.Value);	// 기준년도
	fcd_AddParam("STND_MM", cboStndMonth.Value);	// 기준월
	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);
}

/*-------------------------------------------------------------------------------------------------+
>>  저장 하기전에 필수항목 체크
+-------------------------------------------------------------------------------------------------*/
function fcSaveCheck(obj) {
	// 변경된 자료가 있는지 체크
	if ( !fnc_DatasetChangeCheck(dsOrganExam001) 
		&& !fnc_DatasetChangeCheck(dsOrganExam004)
		&& !fnc_DatasetChangeCheck(dsOrganExam005)
		) {
		// 저장 할 자료가 없음
		fnc_Message(SNIS_COM_1006, "N");
		return false;
	} 

	// 필수항목체크
	oDataSet          = dsOrganExam001;
	sMandatoryColID   = "DIST_DT";
	sMandatoryColName = "일자";
	oGrid             = grdOrganExam001;

	oDataSet          = dsOrganExam004;
	sMandatoryColID   = "DIST_DT";
	sMandatoryColName = "일자";
	oGrid             = grdOrganExam004;

	oDataSet          = dsOrganExam005;
	sMandatoryColID   = "DIST_DT";
	sMandatoryColName = "일자";
	oGrid             = grdOrganExam005;

	if ( fnc_GetMandatoryViolateRow(oDataSet, sMandatoryColID, sMandatoryColName, oGrid) > -1 ) {
		return false;
	}

	fcSetDsOrganExamSave();

	return true;
}

/*-------------------------------------------------------------------------------------------------+
>>  저장 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSave(obj) {

	if ( !fcSaveCheck() ) return;

	var sServiceName = "";
	var sInDataSet   = "";
	var sOutDataSet  = "";

	sServiceName = "rbr5010-service:saveOrganExam";
	sInDataSet   = "dsOrganExamSave=dsOrganExamSave dsOrganExamDelete=dsOrganExamDelete ";
	sOutDataSet  = "dsOrganExam=dsOrganExam ";

	fcd_AddParam("MEET_CD", "003");					// 경륜구분코드	
    fcd_AddParam("STND_YEAR", cboStndYear.Value);	// 기준년도
	fcd_AddParam("STND_MM", cboStndMonth.Value);	// 기준월

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}

function fcCreateCheck(obj) {
	if(fnc_Message("기존에 입력되었던 " + cboStndYear.Value+"년 " + cboStndMonth.Value + "월 데이터는 모두 삭제됩니다.\n"
				+ "전월 데이터를 기준으로 새로 입력하시겠습니까?", "Y")) {
		return true;
	} else {
		return false;
	}	
}

function fcCreate(obj) {
	if(!fcCreateCheck(obj)) return false;

	var sServiceName = "";
	var sInDataSet   = "";
	var sOutDataSet  = "";

	sServiceName = "rbr5010-service:saveDataFromPrev";
	sInDataSet   = "";
	sOutDataSet  = "dsOrganExam=dsOrganExam ";

	fcd_AddParam("MEET_CD", "003");					// 경륜구분코드	
    fcd_AddParam("STND_YEAR", cboStndYear.Value);	// 기준년도
	fcd_AddParam("STND_MM", cboStndMonth.Value);	// 기준월

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}

/***************************************************************************************************
*                          CALL BACK 처리 부분                                                     *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {


	if ( !fnc_result(ErrorCode, ErrorMSG) ) return;

	// 조회 완료 후
	if ( sServiceName == "rbr5010-service:searchOrganExam" ) {
		dsOrganExam.Filter("ORGAN_CD=='001'");	//확정본
		dsOrganExam001.copyF(dsOrganExam);
		dsOrganExam.UnFilter();
		dsOrganExam.Filter("ORGAN_CD=='004'");	//교차
		dsOrganExam004.copyF(dsOrganExam);
		dsOrganExam.UnFilter();
		dsOrganExam.Filter("ORGAN_CD=='005'");	//미확정본
		dsOrganExam005.copyF(dsOrganExam);
		dsOrganExam.UnFilter();
	}		

	// 저장 완료 후
	if ( sServiceName == "rbr5010-service:saveOrganExam" ) {
		fcSearch();  // 다시 조회한다.
	}			

	// 생성 완료 후
	if ( sServiceName == "rbr5010-service:saveDataFromPrev" ) {
		fcSearch();  // 다시 조회한다.
	}	

}


/*-------------------------------------------------------------------------------------------------+
>>  행추가 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcAddRow(obj) {
	// DataSet의 Header 정보가 있을 때  Add 처리
	switch ( obj.GetForm().id ) {
		case "organExam001BtnRow" :
			nRow = dsOrganExam001.AddRow(); // Record Add
			dsOrganExam001.SetColumn(nRow,"STND_YEAR",cboStndYear.Value);
			dsOrganExam001.SetColumn(nRow,"STND_MM",cboStndMonth.Value);
			dsOrganExam001.SetColumn(nRow,"MEET_CD","003");		//경륜
			dsOrganExam001.SetColumn(nRow,"ORGAN_CD","001");	//출주표 확정본	
			break;

		case "organExam004BtnRow" :
			nRow = dsOrganExam004.AddRow(); // Record Add
			dsOrganExam004.SetColumn(nRow,"STND_YEAR",cboStndYear.Value);
			dsOrganExam004.SetColumn(nRow,"STND_MM",cboStndMonth.Value);
			dsOrganExam004.SetColumn(nRow,"MEET_CD","003");		//경륜
			dsOrganExam004.SetColumn(nRow,"ORGAN_CD","004");	//출주표 교차	
			break;

		case "organExam005BtnRow" :
			nRow = dsOrganExam005.AddRow(); // Record Add
			dsOrganExam005.SetColumn(nRow,"STND_YEAR",cboStndYear.Value);
			dsOrganExam005.SetColumn(nRow,"STND_MM",cboStndMonth.Value);
			dsOrganExam005.SetColumn(nRow,"MEET_CD","003");		//경륜
			dsOrganExam005.SetColumn(nRow,"ORGAN_CD","005");	//출주표 미확정
			break;
	}
}


/*-------------------------------------------------------------------------------------------------+
>>  행삽입 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcInsRow(obj) {
	dsOrganExam001.InsertRow(dsOrganExam001.GetCurrow());           // Record Ins
}


/*-------------------------------------------------------------------------------------------------+
>>  행삭제 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcDelRow(obj) {
	// DataSet의 Header 정보가 있을 때  Add 처리
	switch ( obj.GetForm().id ) {
		case "organExam001BtnRow" :
			if ( !fnc_DeleteCheck(dsOrganExam001, "chk", "1") ) return;
			break;
		case "organExam004BtnRow" :
			if ( !fnc_DeleteCheck(dsOrganExam004, "chk", "1") ) return;
			break;
		case "organExam005BtnRow" :
			if ( !fnc_DeleteCheck(dsOrganExam005, "chk", "1") ) return;
			break;		

	}
}

/*-------------------------------------------------------------------------------------------------+
>>  그리드 정렬
+-------------------------------------------------------------------------------------------------*/
function fcGridSort(obj,nCell,nX,nY) {
	// 특정 Head 클릭 시 해당 항목으로 Sort(만약 특정 컬럼을 누를때만 소트하고자 한다면 nCell로 조건)
	if ( nCell == 0 ) {
 		fnc_GridAllChk(obj);
	} else {
		fnc_GridSort(obj, object(obj.BindDataset), nCell);
	}
}


//데이터셋 변경자료가 있는지 체크
function fcCheckDataset(obj) {
	sDataset = split(obj, "|");
	var sMessageCode = "";

	var bChangeYn = false;

	for ( var i = 0; i < sDataset.length(); i++ ) {
		objDataset = Object(sDataset[i]);
		if ( fnc_DatasetChangeCheck(objDataset) ) {
			bChangeYn = true;
			break;
		}
	}

	//변경된 자료가 있을 때 메시지 처리
	if ( bChangeYn ) {
		sMessageCode = SNIS_RBM_B028;

		//화면 종료 여부 확인 
		if ( !fnc_Message(sMessageCode, "Y") ) {
			return false;    //종료하지 않고 Unload Cracel 처리 한다.
		}		
	} 
	return true;
}

function fcOnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	var sDataset = "dsOrganExam001|dsOrganExam004|dsOrganExam005";
	if(fcCheckDataset(sDataset)) {
		fcSearch(obj);
		return true;
	} else {
		obj.index = nOldIndex;
	}
}

function fcSetDsOrganExamSave() {
    dsOrganExamSave.ClearData();
    dsOrganExamDelete.ClearData();
	var i = 0;
	var tmp_cnt = 0;
	if(fnc_DatasetChangeCheck(dsOrganExam001)) {	//변경된 데이터가 있으면
		for ( i = 0; i < dsOrganExam001.GetRowCount(); i++ ) {
			for ( j = 0; j < dsBrncCd.GetRowCount(); j++ ) {
				tmp_cnt = decode(dsOrganExam001.GetColumn(i, dsBrncCd.GetColumn(j, "CD_NM")), null, 0, dsOrganExam001.GetColumn(i, dsBrncCd.GetColumn(j, "CD_NM")));
				idx = dsOrganExamSave.AddRow();
				dsOrganExamSave.SetColumn(idx,"STND_YEAR",	dsOrganExam001.GetColumn(i, "STND_YEAR"));
				dsOrganExamSave.SetColumn(idx,"STND_MM", 	dsOrganExam001.GetColumn(i, "STND_MM"));
				dsOrganExamSave.SetColumn(idx,"DIST_DT", 	dsOrganExam001.GetColumn(i, "DIST_DT"));
				dsOrganExamSave.SetColumn(idx,"MEET_CD", 	dsOrganExam001.GetColumn(i, "MEET_CD"));
				dsOrganExamSave.SetColumn(idx,"ORGAN_CD", 	dsOrganExam001.GetColumn(i, "ORGAN_CD"));
				dsOrganExamSave.SetColumn(idx,"BRNC_CD", 	dsBrncCd.GetColumn(j, "CD"));
				dsOrganExamSave.SetColumn(idx,"DIST_CNT", 	tmp_cnt);
			}	
		}
		idx = dsOrganExamDelete.AddRow();
		if(i > 0) {
			dsOrganExamDelete.SetColumn(idx,"STND_YEAR",dsOrganExam001.GetColumn(i-1, "STND_YEAR"));
			dsOrganExamDelete.SetColumn(idx,"STND_MM", 	dsOrganExam001.GetColumn(i-1, "STND_MM"));
			dsOrganExamDelete.SetColumn(idx,"MEET_CD", 	dsOrganExam001.GetColumn(i-1, "MEET_CD"));
			dsOrganExamDelete.SetColumn(idx,"ORGAN_CD", dsOrganExam001.GetColumn(i-1, "ORGAN_CD"));
		} else {
			dsOrganExamDelete.SetColumn(idx,"STND_YEAR",cboStndYear.Value);
			dsOrganExamDelete.SetColumn(idx,"STND_MM", 	cboStndMonth.Value);
			dsOrganExamDelete.SetColumn(idx,"MEET_CD", 	"003");
			dsOrganExamDelete.SetColumn(idx,"ORGAN_CD", "001");
		}
	}
	if(fnc_DatasetChangeCheck(dsOrganExam004)) {	//변경된 데이터가 있으면
		for ( i = 0; i < dsOrganExam004.GetRowCount(); i++ ) {
			for ( j = 0; j < dsBrncCd.GetRowCount(); j++ ) {
				tmp_cnt = decode(dsOrganExam004.GetColumn(i, dsBrncCd.GetColumn(j, "CD_NM")), null, 0, dsOrganExam004.GetColumn(i, dsBrncCd.GetColumn(j, "CD_NM")));
				idx = dsOrganExamSave.AddRow();
				dsOrganExamSave.SetColumn(idx,"STND_YEAR",	dsOrganExam004.GetColumn(i, "STND_YEAR"));
				dsOrganExamSave.SetColumn(idx,"STND_MM", 	dsOrganExam004.GetColumn(i, "STND_MM"));
				dsOrganExamSave.SetColumn(idx,"DIST_DT", 	dsOrganExam004.GetColumn(i, "DIST_DT"));
				dsOrganExamSave.SetColumn(idx,"MEET_CD", 	dsOrganExam004.GetColumn(i, "MEET_CD"));
				dsOrganExamSave.SetColumn(idx,"ORGAN_CD", 	dsOrganExam004.GetColumn(i, "ORGAN_CD"));
				dsOrganExamSave.SetColumn(idx,"BRNC_CD", 	dsBrncCd.GetColumn(j, "CD"));
				dsOrganExamSave.SetColumn(idx,"DIST_CNT", 	tmp_cnt);
			}	
		}
		idx = dsOrganExamDelete.AddRow();
		if(i > 0) {
			dsOrganExamDelete.SetColumn(idx,"STND_YEAR",dsOrganExam004.GetColumn(i-1, "STND_YEAR"));
			dsOrganExamDelete.SetColumn(idx,"STND_MM", 	dsOrganExam004.GetColumn(i-1, "STND_MM"));
			dsOrganExamDelete.SetColumn(idx,"MEET_CD", 	dsOrganExam004.GetColumn(i-1, "MEET_CD"));
			dsOrganExamDelete.SetColumn(idx,"ORGAN_CD", dsOrganExam004.GetColumn(i-1, "ORGAN_CD"));
		} else {
			dsOrganExamDelete.SetColumn(idx,"STND_YEAR",cboStndYear.Value);
			dsOrganExamDelete.SetColumn(idx,"STND_MM", 	cboStndMonth.Value);
			dsOrganExamDelete.SetColumn(idx,"MEET_CD", 	"003");
			dsOrganExamDelete.SetColumn(idx,"ORGAN_CD", "004");
		}

	}
	if(fnc_DatasetChangeCheck(dsOrganExam005)) {	//변경된 데이터가 있으면
		for ( i = 0; i < dsOrganExam005.GetRowCount(); i++ ) {
			for ( j = 0; j < dsBrncCd.GetRowCount(); j++ ) {
				tmp_cnt = decode(dsOrganExam005.GetColumn(i, dsBrncCd.GetColumn(j, "CD_NM")), null, 0, dsOrganExam005.GetColumn(i, dsBrncCd.GetColumn(j, "CD_NM")));
				idx = dsOrganExamSave.AddRow();
				dsOrganExamSave.SetColumn(idx,"STND_YEAR",	dsOrganExam005.GetColumn(i, "STND_YEAR"));
				dsOrganExamSave.SetColumn(idx,"STND_MM",  	dsOrganExam005.GetColumn(i, "STND_MM"));
				dsOrganExamSave.SetColumn(idx,"DIST_DT",  	dsOrganExam005.GetColumn(i, "DIST_DT"));
				dsOrganExamSave.SetColumn(idx,"MEET_CD",  	dsOrganExam005.GetColumn(i, "MEET_CD"));
				dsOrganExamSave.SetColumn(idx,"ORGAN_CD", 	dsOrganExam005.GetColumn(i, "ORGAN_CD"));
				dsOrganExamSave.SetColumn(idx,"BRNC_CD",  	dsBrncCd.GetColumn(j, "CD"));
				dsOrganExamSave.SetColumn(idx,"DIST_CNT", 	tmp_cnt);
			}	
		}
		idx = dsOrganExamDelete.AddRow();
		if( i > 0 ) {
			dsOrganExamDelete.SetColumn(idx,"STND_YEAR",dsOrganExam005.GetColumn(i-1, "STND_YEAR"));
			dsOrganExamDelete.SetColumn(idx,"STND_MM", 	dsOrganExam005.GetColumn(i-1, "STND_MM"));
			dsOrganExamDelete.SetColumn(idx,"MEET_CD", 	dsOrganExam005.GetColumn(i-1, "MEET_CD"));
			dsOrganExamDelete.SetColumn(idx,"ORGAN_CD", dsOrganExam005.GetColumn(i-1, "ORGAN_CD"));
		} else {
			dsOrganExamDelete.SetColumn(idx,"STND_YEAR",cboStndYear.Value);
			dsOrganExamDelete.SetColumn(idx,"STND_MM", 	cboStndMonth.Value);
			dsOrganExamDelete.SetColumn(idx,"MEET_CD", 	"003");
			dsOrganExamDelete.SetColumn(idx,"ORGAN_CD", "005");
		}
	}
}

