/*-------------------------------------------------------------------------------------------------+
>>  최초 화면 Load時 처리 할 사항
+-------------------------------------------------------------------------------------------------*/
function fcFormLoadSetting(obj) {  
    sBUTTONLIST   = 'TTTFTFT';     // T초기화,T조회,F저장,F삭제,T엑셀,F인쇄,T닫기

 	//화면환경 설정
	sFORMCAPTION  = obj.Title;
	sFORMLOCATION = "";

	//화면 정보 Display 및 Button 권한 설정
	fnc_FormLoadSetting(obj, sFORMCAPTION, sFORMLOCATION, sBUTTONLIST, sUSERAUTH);

    //공통코드 조회
	fcd_GetComboDs(dsInComCd);

	// 기준년도 값 설정
	fcd_SetStndYear(dsStndYear, dsCond.GetColumn(0,"MIN_STND_YEAR"), substr(fcd_GetSysdate("YYYYMMDD"), 0, 4)); 
	cboStndYear.Value = dsCond.GetColumn(0,"STND_YEAR");

	// 기준월 값 설정
	cboStndMonth.Value = fcd_GetSysdate("MM");
	// 부서조회
	fcSearchBrnc();
}

/*-------------------------------------------------------------------------------------------------+
>>  최초 화면 UNLoad時 처리 할 사항
+-------------------------------------------------------------------------------------------------*/
function fcFormUnloadProcess(obj) {
	//자료 변경 여부를 체크하고자 하는  DataSet Object를 넘긴다.
	var sDataset = "dsOrganExamBrncCycle|dsOrganExamBrncBoat";
	if (   !fnc_FormUnloadCheck(sDataset  , sFormId) ) return false;
}

/***************************************************************************************************
*                                      공통 버튼 Event 처리 부분                                   *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  취소 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcInit(obj) {
	dsOrganExamBrnc.ClearData();
    dsOrganExamBrncCycle.ClearData();
    dsOrganExamBrncBoat.ClearData();
    dsOrganExamVeri.ClearData();
    dsBrncInfo.ClearData();
}

/*-------------------------------------------------------------------------------------------------+
>>  화면 종료(닫기) 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcEnd(obj) {
	// 화면 종료
	Close();
}

/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 체크 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearchCheck(obj) {
    //조회조건 체크및 trim처리
    fcInit(obj);    //기존 데이터셋 초기화
    return true;
}

/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearchBrnc(obj) {
    if ( !fcSearchCheck(obj) ) return;

    var sServiceName = "rbr5030-service:searchBrncCd";
    var sInDataSet   = "";
    var sOutDataSet  = "dsBrncInfo=dsBrncInfo ";
    fcd_AddParam("USER_ID", gdsUser.getColumn(0,"USER_ID"));	// 지점코드
	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);
}


/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearch(obj) {
    if ( !fcSearchCheck(obj) ) return;

    var sServiceName = "rbr5030-service:searchOrganExamVeri";
    var sInDataSet   = "";
    var sOutDataSet  = "dsOrganExamVeri=dsOrganExamVeri dsOrganExamBrnc=dsOrganExamBrnc";

    fcd_AddParam("STND_YEAR", cboStndYear.Value);	// 기준년도
	fcd_AddParam("STND_MM", cboStndMonth.Value);	// 기준월
	fcd_AddParam("BRNC_CD", stBrncCd.Text);		// 지점코드
	fcd_AddParam("USER_ID", gdsUser.getColumn(0,"USER_ID"));	// 지점코드
	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);
}

/*-------------------------------------------------------------------------------------------------+
>>  저장 하기전에 필수항목 체크
+-------------------------------------------------------------------------------------------------*/
function fcSaveCheck(obj) {
	// 변경된 자료가 있는지 체크
	if ( !fnc_DatasetChangeCheck(dsOrganExamBrncCycle) 
		&& !fnc_DatasetChangeCheck(dsOrganExamBrncBoat)
		) {
		// 저장 할 자료가 없음
		fnc_Message(SNIS_COM_1006, "N");
		return false;
	}

	// 필수항목체크
	dsOrganExamBrnc.ClearData();
	dsOrganExamBrnc.copy(dsOrganExamBrncCycle);
	dsOrganExamBrnc.appendDataset(dsOrganExamBrncBoat);

	return true;
}

/*-------------------------------------------------------------------------------------------------+
>>  저장 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSave(obj) {

	if ( !fcSaveCheck() ) return;

	var sServiceName = "";
	var sInDataSet   = "";
	var sOutDataSet  = "";

	sServiceName = "rbr5030-service:saveOrganExamBrnc";
	sInDataSet   = "dsOrganExamBrnc=dsOrganExamBrnc ";
	sOutDataSet  = "";

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}

/*-------------------------------------------------------------------------------------------------+
>>  확정 하기전에 필수항목 체크
+-------------------------------------------------------------------------------------------------*/
function fcConfirmCheck(obj) {
	// 경륜 변경된 자료가 있는지 체크
	if(fnc_DatasetChangeCheck(dsOrganExamBrncCycle)) {
		if(!fnc_Message(SNIS_RBM_B029, "Y")) return false;
	}
	// 경정 변경된 자료가 있는지 체크
	if(fnc_DatasetChangeCheck(dsOrganExamBrncCycle)) {
		if(!fnc_Message(SNIS_RBM_B030, "Y")) return false;
	}

	if(btnCfmVeri.Text == '확정') {	//확정할 때만 체크함
		for(var i = 0; i< dsOrganExamBrncCycle.GetRowCount(); i++) {
			if(dsOrganExamBrncCycle.getColumn(i,"VERI_YN") == "N")
				if(!fnc_Message(SNIS_RBM_B031, "Y", i+1)) return false;
		}
		for(var i = 0; i< dsOrganExamBrncBoat.GetRowCount(); i++) {
			if(dsOrganExamBrncBoat.getColumn(i,"VERI_YN") == "N")
				if(!fnc_Message(SNIS_RBM_B032, "Y", i+1)) return false;
		}
	}

	return true;
}
/*-------------------------------------------------------------------------------------------------+
>>  확정 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcConfirm(obj) {

	if ( !fcConfirmCheck() ) return;

	var sServiceName = "";
	var sInDataSet   = "";
	var sOutDataSet  = "";

	sServiceName = "rbr5030-service:saveOrganExamVeri";
	sInDataSet   = "dsOrganExamVeri=dsOrganExamVeri ";
	sOutDataSet  = "";

    //fcd_AddParam("STND_YEAR", cboStndYear.Value);	// 기준년도
	//fcd_AddParam("STND_MM", cboStndMonth.Value);	// 기준월
	//fcd_AddParam("BRNC_CD", stBrncCd.value);		// 지점코드
	//fcd_AddParam("MEET_CD", stMeetCd.value);		// 개최장소
	//fcd_AddParam("USER_ID", gdsUser.getColumn(0,"USER_ID"));	// 지점코드

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}

/***************************************************************************************************
*                          CALL BACK 처리 부분                                                     *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {


	if ( !fnc_result(ErrorCode, ErrorMSG) ) return;

	// 지점코드 조회 완료 후
	if ( sServiceName == "rbr5030-service:searchBrncCd" ) {
		stBrncCd.Value = dsBrncInfo.getColumn(0,"BRNC_CD");
		stBrncNm.Value = dsBrncInfo.getColumn(0,"BRNC_NM");
		stMeetCd.Value = dsBrncInfo.getColumn(0,"MEET_CD");
		fcSearch();
	}	

	// 조회 완료 후
	if ( sServiceName == "rbr5030-service:searchOrganExamVeri" ) {
		dsOrganExamBrnc.Filter("MEET_CD=='001'");	//경륜
		dsOrganExamBrncCycle.copyF(dsOrganExamBrnc);
		dsOrganExamBrnc.UnFilter();
		dsOrganExamBrnc.Filter("MEET_CD=='003'");	//경정
		dsOrganExamBrncBoat.copyF(dsOrganExamBrnc);
		dsOrganExamBrnc.UnFilter();

		//검수상태 설정
		stChgStat.Value = dsOrganExamVeri.getColumn(0,"CHG_VERI_NM");			//담당자 확정상태
		stCfmStat.Value = dsOrganExamVeri.getColumn(0,"CFM_VERI_NM");			//확인자 확정상태
		stMgrCycleStat.Value = dsOrganExamVeri.getColumn(0,"MGR_CYCLE_VERI_NM");//경륜관리자 확정상태
		stMgrBoatStat.Value = dsOrganExamVeri.getColumn(0,"MGR_BOAT_VERI_NM");	//경정관리자 확정상태
		divBtnLst.btnSave.Enable = dsOrganExamVeri.getColumn(0,"SAVE_ENABLE");	//저장버튼 활성화 여부
		btnCfmVeri.Enable = dsOrganExamVeri.getColumn(0,"CFM_ENABLE");			//확정버튼 활성화 여부
		btnCfmVeri.Text = dsOrganExamVeri.getColumn(0,"CFM_STAT");				//확정상태

		if(dsOrganExamVeri.getColumn(0,"BRNC_CD") == '98') {	//경정 담당자일 경우 부산김해를 추가한다.
			idx = dsOrganExamVeri.AddRow();
			dsOrganExamVeri.SetColumn(idx, "BRNC_CD", "29") ;
			dsOrganExamVeri.SetColumn(idx, "CFM_ENABLE", dsOrganExamVeri.getColumn(0,"CFM_ENABLE")) ;
			dsOrganExamVeri.SetColumn(idx, "CFM_STAT", dsOrganExamVeri.getColumn(0,"CFM_STAT")) ;
			dsOrganExamVeri.SetColumn(idx, "CFM_TYPE", dsOrganExamVeri.getColumn(0,"CFM_TYPE")) ;
			dsOrganExamVeri.SetColumn(idx, "CFM_VALUE", dsOrganExamVeri.getColumn(0,"CFM_VALUE")) ;
			dsOrganExamVeri.SetColumn(idx, "CFM_VERI_NM", dsOrganExamVeri.getColumn(0,"CFM_VERI_NM")) ;
			dsOrganExamVeri.SetColumn(idx, "CFM_VERI_YN", dsOrganExamVeri.getColumn(0,"CFM_VERI_YN")) ;
			dsOrganExamVeri.SetColumn(idx, "CHG_VERI_NM", dsOrganExamVeri.getColumn(0,"CHG_VERI_NM")) ;
			dsOrganExamVeri.SetColumn(idx, "CHG_VERI_YN", dsOrganExamVeri.getColumn(0,"CHG_VERI_YN")) ;
			dsOrganExamVeri.SetColumn(idx, "EMP_STAT", dsOrganExamVeri.getColumn(0,"EMP_STAT")) ;
			dsOrganExamVeri.SetColumn(idx, "MGR_BOAT_VERI_NM", dsOrganExamVeri.getColumn(0,"MGR_BOAT_VERI_NM")) ;
			dsOrganExamVeri.SetColumn(idx, "MGR_CYCLE_VERI_NM", dsOrganExamVeri.getColumn(0,"MGR_CYCLE_VERI_NM")) ;
			dsOrganExamVeri.SetColumn(idx, "ORD", dsOrganExamVeri.getColumn(0,"ORD")) ;
			dsOrganExamVeri.SetColumn(idx, "SAVE_ENABLE", dsOrganExamVeri.getColumn(0,"SAVE_ENABLE")) ;
			dsOrganExamVeri.SetColumn(idx, "STND_MM", dsOrganExamVeri.getColumn(0,"STND_MM")) ;
			dsOrganExamVeri.SetColumn(idx, "STND_YEAR", dsOrganExamVeri.getColumn(0,"STND_YEAR")) ;
		}

		//수정여부 설정(담당자일 경우만 수정가능)
		if(dsOrganExamVeri.getColumn(0,"EMP_STAT") == "CHG" and dsOrganExamVeri.getColumn(0,"CHG_VERI_YN") == 'N') {
			grdOrganExamBrncCycle.Editable = "True";
			grdOrganExamBrncBoat.Editable = "True";
		} else {
			grdOrganExamBrncCycle.Editable = "False";
			grdOrganExamBrncBoat.Editable = "False";
		}	
	}	

	// 저장 완료 후
	if ( sServiceName == "rbr5030-service:saveOrganExamBrnc" ) {
		fcSearch();
	}		

	// 확정 완료 후
	if ( sServiceName == "rbr5030-service:saveOrganExamVeri" ) {
		fcSearch();
	}			
}



/*-------------------------------------------------------------------------------------------------+
>>  그리드 정렬
+-------------------------------------------------------------------------------------------------*/
function fcGridSort(obj,nCell,nX,nY) {
	// 특정 Head 클릭 시 해당 항목으로 Sort(만약 특정 컬럼을 누를때만 소트하고자 한다면 nCell로 조건)
	if ( nCell == 0 ) {
 		fnc_GridAllChk(obj);
	} else {
		fnc_GridSort(obj, object(obj.BindDataset), nCell);
	}
}


//데이터셋 변경자료가 있는지 체크
function fcCheckDataset(obj) {
	sDataset = split(obj, "|");
	var sMessageCode = "";

	var bChangeYn = false;

	for ( var i = 0; i < sDataset.length(); i++ ) {
		objDataset = Object(sDataset[i]);
		if ( fnc_DatasetChangeCheck(objDataset) ) {
			bChangeYn = true;
			break;
		}
	}

	//변경된 자료가 있을 때 메시지 처리
	if ( bChangeYn ) {
		sMessageCode = SNIS_RBM_B028;

		//화면 종료 여부 확인 
		if ( !fnc_Message(sMessageCode, "Y") ) {
			return false;    //종료하지 않고 Unload Cracel 처리 한다.
		}		
	} 
	return true;
}

function fcOnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	var sDataset = "dsOrganExamBrncCycle|dsOrganExamBrncBoat";
	if(fcCheckDataset(sDataset)) {
		fcSearch(obj);
		return true;
	} else {
		obj.index = nOldIndex;
	}
}


function fcBtnOnClick(obj)
{
	fcConfirm(obj);
}


