var sCurDate = fcd_GetSysdate("YYYYMMDD");				// 시스템의 오늘날짜
var sCurYear = substr(sCurDate, 0, 4);					// 시스템의 현재년도
var bCodeRefresh    = false;

/***************************************************************************************************
*                                         Form 처리 부분                                           *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  최초 화면 Load時 처리 할 사항
+-------------------------------------------------------------------------------------------------*/
function fcFormLoadSetting(obj) {  
    sBUTTONLIST   = 'TTTFTFT';

 	//화면환경 설정
	sFORMCAPTION  = obj.Title;
	sFORMLOCATION = "";

	//화면 정보 Display 및 Button 권한 설정
	fnc_FormLoadSetting(obj, sFORMCAPTION, sFORMLOCATION, sBUTTONLIST, sUSERAUTH);

    //공통코드 조회
    fcd_GetComboDs(dsInComCd);

    //조회 조건의 날짜(올해 1월1일 ~ today)
    calFromDt.Value = sCurYear + "0101";
    calToDt  .Value = sCurDate;


    //조회 조건의 소모품에는 '전체'를 추가한다.
    if( dsSupplCdSearch.rowcount > 0 ) {
		dsSupplCdSearch.InsertRow(0);
		dsSupplCdSearch.SetColumn(0, "CD_NM", "전체");
	}

	if (gdsUser.GetColumn(0,   "BRNC_CD") == "98") 	rdoGbn.Value = "002";	//경정
	else											rdoGbn.Value = "001";	//경륜

	if (isExistVar("sApro", true) && isExistVar("sMnAuthRmk", true)) {
		if (sApro == "T" && length(trim(sMnAuthRmk)) > 0) {
			rdoGbn.Value = sMnAuthRmk;
			dsSupplCd.Filter("CD_TERM1='"+rdoGbn.Value+"'");
			rdoGbn.Enable = false;
		}
	}
	fcsetManufCol();

	fcSearch();	//시작시 조회
}

/*-------------------------------------------------------------------------------------------------+
>>  최초 화면 UNLoad時 처리 할 사항
+-------------------------------------------------------------------------------------------------*/
function fcFormUnloadProcess(obj) {
	//자료 변경 여부를 체크하고자 하는 DataSet Object를 넘긴다.
	var sDataset = "dsSupplHist";
	if ( !fnc_FormUnloadCheck(sDataset, sFormId) ) return false;
}



/***************************************************************************************************
*                                      업무화면 공통 버튼 Event 처리 부분                          *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  초기화 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcInit(obj) {
	dsSupplStk   .ClearData(); 
    dsSupplHist  .ClearData();
}

/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearchCheck(obj) {
	fcInit(obj);
	return true;
}

function fcSearch(obj) {
    if ( !fcSearchCheck(obj) ) return;

	var sServiceName = "rbs3010-service:searchSupplHist";
    var sInDataSet   = "";
    var sOutDataSet  = "dsSupplStk=dsSupplStk dsSupplHist=dsSupplHist";  

    //조회조건
	fcd_AddParam("SUPPL_CD"    , "");
	fcd_AddParam("PRHS_DT_FROM", calFromDt.Value);
	fcd_AddParam("PRHS_DT_TO"  , calToDt.Value);
	fcd_AddParam("BIZ_GBN"     , rdoGbn.Value);
	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);
}

/*-------------------------------------------------------------------------------------------------+
>>  저장 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/

// 저장 전에 체크
function fcSaveCheck(obj) {  
	//변경된 자료가 있는지 체크
	if ( !fnc_DatasetChangeCheck(dsSupplHist) ) {
		if ( !fnc_DatasetChangeCheck(dsSupplStk) ) {
			//저장 할 자료가 없음
			fnc_Message(SNIS_COM_1006, "N");	
			return false;
		}
	}
	if ( fnc_DatasetChangeCheck(dsSupplStk) ) {
		bCodeRefresh = true;
	}

	// 필수항목체크
	oDataSet          = dsSupplHist;
	sMandatoryColID   = "PRHS_DT|SUPPL_CD|QTY";
	sMandatoryColName = "입고일자|소모품|수량";
	oGrid             = grdSupplHist;

	if ( fnc_GetMandatoryViolateRow(oDataSet, sMandatoryColID, sMandatoryColName, oGrid) > -1 ) {
	    return false;
	}

	// 필수항목체크
	oDataSet          = dsSupplStk;
	sMandatoryColID   = "SUPPL_CD_NM";
	sMandatoryColName = "소모품명";
	oGrid             = grdSupplStk;

	if ( fnc_GetMandatoryViolateRow(oDataSet, sMandatoryColID, sMandatoryColName, oGrid) > -1 ) {
	    return false;
	}

	return true;
}

function fcSave(obj) {

    if ( !fcSaveCheck() ) return;   

    if(fnc_Message(SNIS_COM_1012, "Y")) {           
		var sServiceName = "rbs3010-service:saveSupplStk";
		var sInDataSet   = "dsSupplHist=dsSupplHist:u dsSupplStk=dsSupplStk:u ";
		var sOutDataSet  = "dsSupplHist=dsSupplHist ";  	
		fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
    }
}

