/*-------------------------------------------------------------------------------------------------+
>>  행추가 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcAddRow(obj) {
	var nRow = -1;
	switch (obj.GetForm().id) {
		case "SuppBtnRow":
			nRow = dsSupplHist.AddRow();

			//자동 Setting
			dsSupplHist.SetColumn(nRow, "PRHS_DT",  sCurDate);
			dsSupplHist.SetColumn(nRow, "BIZ_GBN",  rdoGbn.Value);

			grdSupplHist.SetCellPos(2);
			grdSupplHist.SetFocus(); // 입력 첫 항목으로 Focus
			break;

		case "SuppListBtnRow":
			nRow = dsSupplStk.AddRow();
			dsSupplStk.SetColumn(nRow, "QTY", 0);
			dsSupplStk.SetColumn(nRow, "BIZ_GBN",  rdoGbn.Value);
			dsSupplStk.SetColumn(nRow, "HIST_CNT", 0);
			dsSupplStk.SetCellPos(1);
			dsSupplStk.SetFocus(); // 입력 첫 항목으로 Focus
			break;
	}
}

/*-------------------------------------------------------------------------------------------------+
>>  행삽입 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcInsRow(obj) {
	var nRow  = dsSupplHist.InsertRow(dsSupplHist.GetCurrow());

	//자동 Setting
	dsSupplHist.SetColumn(nRow, "PRHS_DT", sCurDate);

	grdSupplHist.SetFocus(); // 입력 첫 항목으로 Focus
}

/*-------------------------------------------------------------------------------------------------+
>>  행삭제 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcDelRow(obj) {
	 switch (obj.GetForm().id) {
		 case "SuppBtnRow":
			if ( !fnc_DeleteCheck(dsSupplHist, "chk", "1") ) return;
			break;

		case "SuppListBtnRow":
			for(var icnt=0;icnt<dsSupplStk.rowcount();icnt++) {
				if (dsSupplStk.GetColumn(icnt, "chk") == "1") {
					if (dsSupplStk.GetColumn(icnt, "HIST_CNT") > 0) {
						fnc_Message(SNIS_RBM_E024, "N", dsSupplStk.GetColumn(icnt, "SUPPL_CD_NM"));
						return;
					}
				}
			}

			if ( !fnc_DeleteCheck(dsSupplStk, "chk", "1") ) return;
			break;
	 }	
}


