/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {
	if ( !fnc_result(ErrorCode, ErrorMSG) ) return;

	if (sServiceName == "common-service:searchCode") {
		//TrACE(dsConsumType.savexml());
	}
	// 조회 완료 후
	if ( sServiceName == "rbs3010-service:searchSupplStk" ) {

	}

	// 저장 완료 후
	if ( sServiceName == "rbs3010-service:saveSupplStk" ) {
		if (bCodeRefresh == true) {
			//공통코드 조회
			fcd_GetComboDs(dsInComCd);
    	}
		fcSearch();		
	}	
}

