			break;
	}
}


function rdoGbn_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	dsSupplCd.Filter("CD_TERM1='"+rdoGbn.Value+"'");
	fcsetManufCol();
	fcSearch();
}

function fcsetManufCol()
{
	if (rdoGbn.Value == "002") {
		grdSupplStk.SetColProp(1, "Width", 80);
	} else {
		grdSupplStk.SetColProp(1, "Width", 0);
	}
}
