function fcInit(obj) {
	dsSupplReq.ClearData(); 

	dsSupplRegi.ClearData();
}

/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearchCheck(obj) {
	edReqCntntSearch.Text = Trim(edReqCntntSearch.Text);
	fcInit(obj);
	return true;
}

function fcSearch(obj) {
    if ( !fcSearchCheck(obj) ) return;

	var sServiceName = "rbs3020-service:searchSupplReq";
    var sInDataSet   = "";
    var sOutDataSet  = "dsSupplReq=dsSupplReq ";  

    //조회조건
	fcd_AddParam("REQ_DT_FROM", calFromDt.Value);
	fcd_AddParam("REQ_DT_TO"  , calToDt.Value);
	fcd_AddParam("REQ_CNTNT"  , edReqCntntSearch.Text);
	fcd_AddParam("REQ_ID"   , gdsUser.GetColumn(gdsUser.currow, "USER_ID"));    

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);
}

function fcSearchNotAprv() {
	var sServiceName = "rbs3020-service:searchNotAprv";
    var sInDataSet   = "";
    var sOutDataSet  = "dsNotAprv=dsNotAprv ";  

    //조회조건
	fcd_AddParam("REQ_ID"   , gdsUser.GetColumn(gdsUser.currow, "USER_ID"));    

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);
}
/*-------------------------------------------------------------------------------------------------+
>>  저장 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/

// 저장 전에 체크
function fcSaveCheck(obj) {  
	//변경된 자료가 있는지 체크
	if ( !fnc_DatasetChangeCheck(dsSupplReq) ) {
		//저장 할 자료가 없음
		fnc_Message(SNIS_COM_1006, "N");	
		return false;
	}

	// 필수항목체크
	oDataSet          = dsSupplReq;
	sMandatoryColID   = "REQ_DT|REQ_CNTNT";
	sMandatoryColName = "신청일자|신청내용";
	oGrid             = grdSupplReq;

	if ( fnc_GetMandatoryViolateRow(oDataSet, sMandatoryColID, sMandatoryColName, oGrid) > -1 ) {
	    return false;
	}
	return true;
}

function fcSave(obj) {

    if ( !fcSaveCheck() ) return;   

    if(fnc_Message(SNIS_COM_1012, "Y")) {           
		var sServiceName = "rbs3020-service:saveSupplReq";
		var sInDataSet   = "dsSupplReq=dsSupplReq:u ";
		var sOutDataSet  = "dsSupplReq=dsSupplReq ";  	

		fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
    }
}

function fcRegiSearch(obj) { 
	var sServiceName = "rbs3030-service:searchSupplRegi";
    var sInDataSet   = "";
    var sOutDataSet  = "dsSupplRegi=dsSupplRegi ";  

    if( dsSupplReq.currow >= 0 ) { 
		//조회조건
		fcd_AddParam("REQ_DT"  , dsSupplReq.GetColumn(dsSupplReq.currow, "REQ_DT"));
		fcd_AddParam("REQ_ID", dsSupplReq.GetColumn(dsSupplReq.currow, "REQ_ID"));
		fcd_AddParam("SEQ"     , dsSupplReq.GetColumn(dsSupplReq.currow, "SEQ"));

		fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
	}
}

function fcSignSave(){
   // if(fnc_Message(SNIS_RBM_E016, "Y")) {           
		var sServiceName = "rbs3020-service:saveSupplReqSign";
		var sInDataSet   = "dsSupplRegi=dsSupplRegi:u ";
		var sOutDataSet  = "dsSupplRegi=dsSupplRegi ";  	


		fcd_AddParam("SIGN_DT"  , fcd_GetSysdate("YYYYMMDD"));										   //서명일자
		fcd_AddParam("USER_ID"  , gdsUser.GetColumn(gdsUser.currow, "USER_ID"));										   //서명자ID

		fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);

   // }
}
