/*-------------------------------------------------------------------------------------------------+
>>  행추가 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcAddRow(obj) {
	//DataSet의 Header 정보가 있을 때  Add 처리	
	var nRow = dsSupplReq.AddRow();

	//자동 Setting
	dsSupplReq.SetColumn(nRow, "REQ_DT"      , sCurDate);
	dsSupplReq.SetColumn(nRow, "MNG_REQ_YN"  , "N");	//관리자신청
	dsSupplReq.SetColumn(nRow, "REQ_ID"    , gdsUser.GetColumn(gdsUser.currow, "USER_ID"));
	dsSupplReq.SetColumn(nRow, "USER_NAME"   , gdsUser.GetColumn(gdsUser.currow, "USER_NM"));
	dsSupplReq.SetColumn(nRow, "PROG_STAT_CD", "001");	//신청으로 Setting
	dsSupplReq.SetColumn(nRow, "USER_TEAM_NM"   , gdsUser.GetColumn(gdsUser.currow, "TEAM_NM"));
	if (gdsUser.GetColumn(0,   "BRNC_CD") == "98") 	dsSupplReq.SetColumn(nRow, "BIZ_GBN", "002");	//경정
	else											dsSupplReq.SetColumn(nRow, "BIZ_GBN", "001");	//경륜
	//taReqCntnt.Enable = true;
	taReqCntnt.SetFocus(); // 입력 첫 항목으로 Focus
}

/*-------------------------------------------------------------------------------------------------+
>>  행삭제 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcDelRow(obj) {	

	sMsg = "";

	for(var i = 0 ; i < dsSupplReq.rowcount ; i++ ) {
		//Chcek된 항목 중 승인여부가 '승인'이면 삭제 불가능
		if( dsSupplReq.GetColumn(i, "CHK") == "1" && 
			dsSupplReq.GetColumn(i, "PROG_STAT_CD") == "003") {
			sMsg += (i+1) + ", ";
			dsSupplReq.SetColumn(i, "CHK", "0");	
		}
	}

	if( sMsg == "" ) {
		if ( !fnc_DeleteCheck(dsSupplReq, "CHK", "1") ) return;
	} else {
		sMsg = LEFT(sMsg, LENGTH(sMsg)-2); 
		fnc_Message(SNIS_RBM_B001, "N", sMsg, "승인된 행");
	}	
}


