/*-------------------------------------------------------------------------------------------------+
>>  그리드 정렬
+-------------------------------------------------------------------------------------------------*/
function fcGridSort(obj,nCell,nX,nY) {
	//특정 Head 클릭 시 해당 항목으로 Sort(만약 특정 컬럼을 누를때만 소트하고자 한다면 nCell로 조건)
	if ( nCell == 0 ) {


 		if(obj.id=="grdSupplRegi"){
			 	var dsChkObj = object(obj.BindDataset);
				if (obj.GetCellProp("head",0,"text") == 1){
					obj.SetCellProp("head",0,"text", "0");
					for ( var i = 0; i< dsChkObj.getRowCount(); i++) {
						dsChkObj.SetColumn(i, "CHK" , 0);
					}
				} else {
					obj.SetCellProp("head",0,"text", "1");
					for ( var i = 0; i< dsChkObj.getRowCount(); i++) {

					   if( Length(dsChkObj.getColumn(i,"SIGN_DT")) <= 0 ){
							dsChkObj.SetColumn(i, "CHK" , 1);
					   }
					}
				}
 		}else{
			fnc_GridAllChk(obj);
 		}

	} else {
		fnc_GridSort(obj, object(obj.BindDataset), nCell);
	}
}

